import React, { useState, useEffect, useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import styled from "styled-components";
import Controls from "./Controls";
import Test from "./Test";
import TestResult from "./TestResult";
import { closeTimeInput, closeTimeMenu } from "../state/action-creators";

function Home() {
  const dispatch = useDispatch();

  const showResult = useSelector((state) => state.showResult);

  const timeInput = useSelector((state) => state.controlsStates.timeInput);
  const timeMenu = useSelector((state) => state.controlsStates.timeMenu);

  useEffect(() => {
    console.log(timeInput);
    console.log(timeMenu);
  }, [timeInput, timeMenu]);

  function handleClickOutside(event) {
    if (
      !document.getElementById("TimeControls").contains(event.target) &&
      !event.target.matches("#custom-time-input, #custom-time-input *")
    ) {
      console.log(timeInput || timeMenu);
      // dispatch(closeTimeMenu());
      // dispatch(closeTimeInput());
    }
  }

  // useEffect(() => {
  //   document.addEventListener("mousedown", handleClickOutside, true);
  // }, []);
  return (
    <Container onClick={(event)=>handleClickOutSide()}>
      <Controls />
      {showResult ? <TestResult /> : <Test />}
    </Container>
  );
}

export default Home;

const Container = styled.div`
  min-height: calc(100vh - 140px);
`;
