import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import {
  firstWord,
  nextWord,
  prevWord,
  updateInput,
  resetInput,
  focusInput,
  blurInput,
  updateCurrentWord,
  updateAndChangeCurrentWord
} from "../state/action-creators/index";

function TextInput(props) {
  const currentWord = useSelector((state) => state.currentWord);
  const enteredWord = useSelector((state) => state.enteredWord);
  const testWords = useSelector((state) => state.testWords);
  const inputFocused = useSelector((state) => state.inputFocused);
  const dispatch = useDispatch();
  const inputType =
    localStorage.getItem("input") === null
      ? "FLOAT"
      : localStorage.getItem("input");

  const testContentScrollerCount = useRef(1);
  const prevWordAtScroll = useRef("");

  var textInput = document.getElementById("textInput");

  useEffect(() => {
    if (currentWord.key > 0) {
      textInput.focus();
      if (currentWord.key > 1) {
        if (
          document.getElementById(currentWord.id).offsetLeft -
            document.getElementById(currentWord.id).parentElement.offsetLeft ==
            0 &&
          currentWord.word != prevWordAtScroll.current
        ) {
          testContentScroller();
        }
      }
    }
  }, [currentWord]);

  useEffect(() => {
    dispatch(firstWord());
    dispatch(focusInput());
  }, []);

  useEffect(() => {
    if (currentWord.key > 0) {
      if (inputFocused) {
        textInput.focus()
      } else {
        textInput.blur();
      }
    }
  }, [inputFocused]);

  useEffect(() => {
    if (currentWord.key != 0) {
      dispatch(updateCurrentWord(currentWord,enteredWord))
    }
  }, [enteredWord]);

  function testContentScroller() {
    let currentLineHeight = parseInt(
      window
        .getComputedStyle(document.getElementById("testContent"))
        .getPropertyValue("line-height")
    );
    document.getElementById(
      "testContentChild"
    ).style.transform = `translateY(-${
      currentLineHeight * testContentScrollerCount.current
    }px)`;
    testContentScrollerCount.current++;
    prevWordAtScroll.current = currentWord.word;
  }

  function textInputChangeHandler(e) {
    let enteredValue = String(e.target.value.replace(/\s/g, ""));
    dispatch(updateInput(enteredValue, currentWord.word));
  }

  function keyDownHandler(e) {
    let keyClicked = e.nativeEvent.code;
    let enteredValue = String(e.target.value.replace(/\s/g, ""));
    if (keyClicked == "Space" && enteredValue != "") {
      // dispatch(
      //   updateCurrentWordForward(enteredWord.word, currentWord, inputType)
      // );
      // dispatch(skipCurrentWordForward(currentWord,enteredWord))
      dispatch(updateAndChangeCurrentWord(currentWord,enteredWord))
      dispatch(nextWord());
      dispatch(resetInput());
    }
  }

  return (
    <Container
      width={`${currentWord.width}px`}
      left={`${currentWord.left}px`}
      inputType={inputType}
    >
      <input
        id="textInput"
        autoCapitalize="off"
        autoComplete="off"
        spellCheck="false"
        maxLength="15"
        value={enteredWord.word}
        onKeyDown={(e) => keyDownHandler(e)}
        onChange={(e) => setTimeout(textInputChangeHandler(e), 1)}
      ></input>
    </Container>
  );
}

export default TextInput;

const Container = styled.div`
  width: 100%;
  position:"absolute";
  input {
    background-color: transparent;
    border: none;
    border-bottom:   "none" ;
    caret-color: "transparent" ;
    font-size: 1.2em;
    font-family: "Quicksand", sans-serif;
    font-weight: 500;
    text-align: center;
    margin: 10px 0 20px;
    padding: 3px 3px 5px;
    color: ${(props) =>
      props.inputType == "INLINE" ? "transparent" : "#ffff"};
    width: ${(props) => props.width};
    transform: translateX(${(props) => props.left});
    transition: all 150ms ease-in-out;

    &:focus {
      outline: none;
    }
  }
`;
