import React, { useEffect, useState, forwardRef } from "react";
import styled from "styled-components";
import { Fade } from "react-reveal";
import HeadShake from "react-reveal/HeadShake";

function CustomTime(props) {
  // states and state managers

  const [time, setTime] = useState(
    localStorage.getItem("time") == 30 || localStorage.getItem("time") == 60
      ? { minutes: 0, seconds: 0 }
      : props.calculateTime(localStorage.getItem("time"), "for-state")
  );

  const [MinuteInputError, setMinuteInputError] = useState(false);
  const [SecondInputError, setSecondInputError] = useState(false);

  if (MinuteInputError || SecondInputError) {
    setTimeout(() => {
      setMinuteInputError(false);
      setSecondInputError(false);
    }, 100);
  }

  // to ensure second's input stays zero when minutes is set to 60

  useEffect(() => {
    if (time.minutes == 60 && time.seconds > 0) {
      setTime((prevTime) => ({ ...prevTime, seconds: 0 }));
      inputError("seconds");
    }
  }, [time]);

  // event handlers

  function inputError(t) {
    switch (t) {
      case "minutes":
        setMinuteInputError(true);
        break;
      case "seconds":
        setSecondInputError(true);
        break;
      default:
        break;
    }
  }

  function valueController(m, l, v, t) {
    let r;
    if (v >= m && v <= l) {
      r = v;
    } else if (v > l) {
      r = l;
      inputError(t);
    } else if (v < m) {
      r = m;
      inputError(t);
    } else {
      r = 0;
    }
    return r;
  }

  function prefixAllocater(number) {
    let r;
    if (number > 9) {
      r = number;
    } else {
      r = `0${number}`;
    }
    return r;
  }

  function timeUpdate(e, t, c) {
    e.stopPropagation();
    switch (c) {
      case "custom":
        setTime((prevTime) => ({
          ...prevTime,
          [t]: valueController(0, 60, parseInt(e.target.value), t),
        }));
        break;
      case "increment":
        setTime((prevTime) => ({
          ...prevTime,
          [t]: valueController(0, 60, parseInt(prevTime[t]) + 1, t),
        }));
        break;
      case "decrement":
        setTime((prevTime) => ({
          ...prevTime,
          [t]: valueController(0, 60, parseInt(prevTime[t]) - 1, t),
        }));
        break;
      case "reset":
        setTime((prevTime) => ({ ...prevTime, [t]: "" }));
        break;
      case "fill":
        setTime((prevTime) => ({ ...prevTime, [t]: 0 }));
        break;
      default:
        break;
    }
  }

  function keyDownHandler(event) {
    switch (event.key) {
      case "Escape":
        props.closeTimeInput();
        break;
      case "Enter":
        props.submitInput(
          parseInt(time["minutes"] * 60) + parseInt(time["seconds"])
        );
        break;
      default:
        break;
    }
  }
  console.log(time);
  return (
    <Container
      tabIndex="0"
      onKeyDown={(event) => {
        keyDownHandler(event);
      }}
    >
      <Fade bottom duration={250}>
        <TimeInput id="custom-time-input">
          <CloseTimeInput
            src="icons/close.png"
            className="cyan-icon"
            onClick={() => props.closeTimeInput()}
          />

          <TimeInputFixedOptions>
            <Time0
              // onClick={() => timeOptionClickHandler(0)}
              className={time == 0 ? "selected-time-option" : ""}
            >
              0s
            </Time0>

            <Time30
              // onClick={() => timeOptionClickHandler(30)}
              className={time == 30 ? "selected-time-option" : ""}
            >
              30s
            </Time30>

            <Time60
              // onClick={() => timeOptionClickHandler(60)}
              className={time == 60 ? "selected-time-option" : ""}
            >
              60s
            </Time60>
          </TimeInputFixedOptions>

          <TimeInputUI>
            <MinuteBlock>
              <img
                src="/icons/up-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) => timeUpdate(event, "minutes", "increment")}
              />

              <HeadShake when={MinuteInputError} duration={400}>
                <MinuteInput
                  type="number"
                  value={time.minutes}
                  onFocus={(event) => {
                    timeUpdate(event, "minutes", "reset");
                  }}
                  onBlur={
                    time.minutes === null
                      ? (event) => timeUpdate(event, "minutes", "fill")
                      : (event) => timeUpdate(event, "minutes", "custom")
                  }
                  onChange={(event) => {
                    timeUpdate(event, "minutes", "custom");
                  }}
                />
              </HeadShake>

              <img
                src="/icons/down-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) => timeUpdate(event, "minutes", "decrement")}
              />
            </MinuteBlock>

            <span>m</span>
            <span className="time-input-divider">:</span>

            <SecondBlock>
              <img
                src="/icons/up-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) => timeUpdate(event, "seconds", "increment")}
              />

              <HeadShake when={SecondInputError} duration={400}>
                <SecondInput
                  type="number"
                  value={time.seconds}
                  onFocus={(event) => timeUpdate(event, "seconds", "reset")}
                  onBlur={
                    time.seconds
                      ? (event) => timeUpdate(event, "seconds", "custom")
                      : (event) => timeUpdate(event, "seconds", "fill")
                  }
                  onChange={(event) => timeUpdate(event, "seconds", "custom")}
                />
              </HeadShake>

              <img
                src="/icons/down-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) => timeUpdate(event, "seconds", "decrement")}
              />
            </SecondBlock>

            <span>s</span>
          </TimeInputUI>

          <Uploadtime
            onClick={() =>
              props.submitInput(
                parseInt(time["minutes"] * 60) + parseInt(time["seconds"])
              )
            }
          >
            OK
          </Uploadtime>
        </TimeInput>
      </Fade>
    </Container>
  );
}

export default CustomTime;

// styles

const Container = styled.div`
  position: absolute;
  right: 0;
  left: 0;
  top: 0;
  bottom: 0;
  z-index: 3;

  display: flex;
  justify-content: center;
  align-items: center;

  backdrop-filter: blur(3px);
  transition: all 250ms ease-in-out;
`;

const TimeInputFixedOptions = styled.div`
  display: flex;
  justify-content: space-evenly;
  width: 300px;
  border-bottom: 1px solid;
  p {
    cursor: pointer;
    margin: 20px 0 10px;
  }
`;

const Time30 = styled.p``;

const Time60 = styled.p``;

const Time0 = styled.p``;

const TimeInput = styled.div`
  position: relative;
  background-color: #17171a;
  max-width: 40vw;
  width: 300px;
  padding: 15px;
  border-radius: 5px;

  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  box-shadow: -1px 10px 23px 0px rgba(0, 0, 0, 0.51);
  -webkit-box-shadow: -1px 10px 23px 0px rgba(0, 0, 0, 0.51);
  -moz-box-shadow: -1px 10px 23px 0px rgba(0, 0, 0, 0.51);
`;

const CloseTimeInput = styled.img`
  position: absolute;
  width: 12px;
  right: 15px;
  top: 15px;
  cursor: pointer;
`;
const TimeInputUI = styled.div`
  width: 100%;
  padding: 20px 5px 30px 5px;

  display: flex;
  justify-content: center;
  align-items: center;

  span {
    margin: 0 0 0 10px;
    font-size: 1.5em;
  }

  .arrow-icon {
    width: 22px;
  }

  .time-input-divider {
    margin: 0 10px;
    color: #00ffff99;
    font-size: 1.5em;
  }

  img {
    cursor: pointer;
  }

  input {
    background-color: transparent;
    border: none;
    color: #aaaaaa;
    font-size: 1.5em;
    text-align: center;
    width: 2ch;
  }
  input:focus {
    outline: none;
  }
  input::-webkit-outer-spin-button,
  input::-webkit-inner-spin-button {
    -webkit-appearance: none;
  }
  input[type="number"] {
    -moz-appearance: textfield;
  }

  @media (max-width: 430px) {
    span {
      font-size: 1em;
    }
    input {
      font-size: 1em;
    }
    .arrow-icon {
      width: 18px;
    }
  }
`;
const MinuteBlock = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const MinuteInput = styled.input``;

const SecondBlock = styled(MinuteBlock)``;

const SecondInput = styled(MinuteInput)``;

const Uploadtime = styled.p`
  position: absolute;
  bottom: 0px;
  left: auto;
  opacity: 0.75;
  color: cyan;
  cursor: pointer;
`;
