import React from "react";
import styled from "styled-components";
import words from "./wordsData";

function Test() {
  function testWordsLoader() {
    let j = 500;
    let wordList = [];
    let prevRanNum = 0;
    for (let i = 0; i < j; i++) {
      var r = Math.floor(Math.random() * words.length);
      if (r == prevRanNum) {
        r++;
      }
      const word = words[r];
      let letterArray = [];
      for (let l = 0; l < word.length; l++) {
        letterArray.push(
          <span key={`word-${i + 1}-letter-${l + 1}`} className="letter">
            {word[l]}
          </span>
        );
      }
      wordList.push(
        <span className="test-words" id={`word-${i + 1}`} key={`word-${i + 1}`}>
          {letterArray}{" "}
        </span>
      );
    }
    return wordList;
  }
  return (
    <Container>
      <TextInput></TextInput>
      <TestContent>{testWordsLoader()}</TestContent>
    </Container>
  );
}

export default Test;

const Container = styled.div``;
const TextInput = styled.input``;

const TestContent = styled.div`
  height: 80px;
  overflow: hidden;
  font-size: 1.25em;
  line-height: 40px;

  .test-words {
    padding: 3px 3px 5px 3px;
    margin: 5px 5px 20px 5px;
  }
`;
