export const confirmWord = () => {
  return { type: "CONFIRM_WORD" };
};

export const nextWord = () => {
  return { type: "NEXT_WORD" };
};

export const prevWord = () => {
  return { type: "PREVIOUS_WORD" };
};

export const updateInput = (value) => {
  return { type: "UPDATE_INPUT", payload: value};
};

export const resetInput = () => {
  return { type: "RESET_INPUT", payload: "" };
};

export const newWords = ()=>{
  return {}
}
