import React, { useState , useEffect} from "react";
import styled from "styled-components";
import withReveal from "react-reveal/withReveal";
import Fade from "react-reveal/Fade";
import Pulse from "react-reveal/Pulse";
import { resetInput } from "../state/action-creators";


function Settings() {
  useEffect(()=>{
    resetInput
  },[])
  const [inputType, setInputType] = useState(localStorage.getItem("input"));
  console.log(localStorage.getItem("input"));

  function optionClickHandler(option, setOption, item, value) {
    setOption(value);
    localStorage.setItem(item, value);
  }

  return (
    <Container>
      <TestMode>
        <Heading>Mode</Heading>
        <OptionBlock>
          <Option
            onClick={() =>
              optionClickHandler(inputType, setInputType, "input", "FLOAT")
            }
            className={inputType == "FLOAT" ? "selected-settings-option" : ""}
          >
            Float
          </Option>
          <Option
            onClick={() =>
              optionClickHandler(inputType, setInputType, "input", "INLINE")
            }
            className={inputType == "INLINE" ? "selected-settings-option" : ""}
          >
            Inline
          </Option>
        </OptionBlock>
      </TestMode>
    </Container>
  );
}

export default Settings;

const Container = styled.div`
  width: 100%;
  min-height: calc(100vh - 140px);
  padding-top: 50px;
`;
const TestMode = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
`;

const Heading = withReveal(
  styled.span`
    font-size: 2rem;
  `,
  <Fade bottom duration={300} />
);

const OptionBlock = styled.div`
  display: flex;
  padding: 20px 0 0 10%;

  .selected-settings-option {
    background-color: #aaa;
    color: #1b1b1e;
  }
`;

const Option = withReveal(
  styled.span`
    font-size: 1.5rem;
    margin: 0 10px;
    padding: 3px 7px;
    border: 1px solid #aaa;
    border-radius: 5px;
    cursor: pointer;
  `,
  <Fade top duration={300} />
);
