const reducer = (
  // state = [
  //   localStorage.getItem("time") === null
  //     ? 30
  //     : parseInt(localStorage.getItem("time")),
  // ],
  state = {
    time:
      localStorage.getItem("time") === null
        ? 30
        : parseInt(localStorage.getItem("time")),
    liveTime:       localStorage.getItem("time") === null
    ? 30
    : parseInt(localStorage.getItem("time")),
  },
  action
) => {
  switch (action.type) {
    case "RESET_TEST_TIMER":
      return [
        localStorage.getItem("time") === null
          ? 30
          : parseInt(localStorage.getItem("time")),
      ];
    case "DECREMENT_TEST_TIMER":
      return state - 1;
    default:
      return state;
  }
};

export default reducer;
