import React, { useState, useEffect } from "react";
import styled from "styled-components";

function TextInput(props) {
  const [currentWord, setCurrentWord] = useState();
  useEffect(
    () => setCurrentWord(document.getElementById("word-1").innerText),
    []
  );
  function textInputEvaluater(e) {
    let enteredValue = e.target.value.replace(/\s/g, "");
    document.getElementById("word-1").classList.remove("right-warning");
    document.getElementById("word-1").classList.remove("wrong-warning");
    if (enteredValue != currentWord.slice(0, enteredValue.length)) {
      document.getElementById("word-1").classList.add("wrong-warning");
    } else if (enteredValue == currentWord) {
      document.getElementById("word-1").classList.add("right-warning");
    }
  }

  return <Container onChange={(e) => textInputEvaluater(e)}></Container>;
}

export default TextInput;

const Container = styled.input`
  position: absolute;
  top: 0;
  background-color: transparent;
  border: none;
  border-bottom: #aaa solid 2px;
  caret-color: #ffff;
  font-size: 1.2em;
  text-align: center;
  margin: 10px 0 0;
  padding: 3px 3px 5px;
  color: #ffff;
  width: ${(props) => props.width};

  &:focus {
    outline: none;
  }
`;
