const reducer = (
  state = { key: 0, index: 0, id: "", word: "", width: 0, left: 0 },
  action
) => {
  switch (action.type) {
    case "NEXT_WORD":
      return loadWord(state, action.payload.testWordsArray, "NEXT");
    case "CONFIRM_WORD":
      return loadWord(
        action.payload.currentWord,
        action.payload.testWordsArray,
        "CONFIRM"
      );
    case "PREVIOUS_WORD":
      return loadWord(state, action.payload.testWordsArray, "PREVIOUS");
    case "FIRST_WORD":
      return loadWord(state, action.payload.testWordsArray, "FIRST");
    case "RESET_WORD":
      return { key: 0, index: 0, id: "", word: "", width: 0, left: 0 };
    default:
      return state;
  }
};

export default reducer;

function loadWord(state, testWordsArray, type) {
  switch (type) {
    case "CONFIRM":
      return {
        key: state.key,
        index: state.index,
        id: state.id,
        word: state.word,
        width: document.getElementById(state.id).offsetWidth,
        left:
          document.getElementById(state.id).offsetLeft -
          document.getElementById(state.id).parentElement.offsetLeft,
        top:
          document.getElementById(state.id).offsetTop -
          document.getElementById(state.id).parentElement.offsetTop,
      };
    case "NEXT":
      return {
        key: state.key + 1,
        index: state.index + 2,
        id: `word-${state.key + 1}`,
        // word: document.getElementById(`word-${state.key + 1}`).innerText,
        word: testWordsArray[state.key + 1],
        width: document.getElementById(`word-${state.key + 1}`).offsetWidth,
        left:
          document.getElementById(`word-${state.key + 1}`).offsetLeft -
          document.getElementById(`word-${state.key + 1}`).parentElement
            .offsetLeft,
        top:
          document.getElementById(`word-${state.key + 1}`).offsetTop -
          document.getElementById(`word-${state.key + 1}`).parentElement
            .offsetTop,
      };
    case "PREVIOUS":
      return {
        key: state.key - 1,
        index: state.index - 2,
        id: `word-${state.key - 1}`,
        word: document.getElementById(`word-${state.key - 1}`).innerText,
        width: document.getElementById(`word-${state.key - 1}`).offsetWidth,
        left:
          document.getElementById(`word-${state.key - 1}`).offsetLeft -
          document.getElementById(`word-${state.key - 1}`).parentElement
            .offsetLeft,
        top:
          document.getElementById(`word-${state.key - 1}`).offsetTop -
          document.getElementById(`word-${state.key - 1}`).parentElement
            .offsetTop,
      };
    case "FIRST":
      return {
        key: 1,
        index: 0,
        id: "word-1",
        word: document.getElementById("word-1").innerText,
        width: document.getElementById("word-1").offsetWidth,
        left:
          document.getElementById("word-1").offsetLeft -
          document.getElementById("word-1").parentElement.offsetLeft,
        top:
          document.getElementById("word-1").offsetTop -
          document.getElementById("word-1").parentElement.offsetTop,
      };
    default:
      return state;
  }
}
