import React, { useState, useEffect } from "react";
import styled from "styled-components";
import withReveal from "react-reveal/withReveal";
import Fade from "react-reveal/Fade";
import Pulse from "react-reveal/Pulse";
import {
  resetInput,
  resetLiveTime,
  resetTest,
  resetWord,
} from "../state/action-creators";
import { useDispatch } from "react-redux";

function Settings() {
  const dispatch = useDispatch();
  useEffect(() => {
    resetTest(dispatch);
  }, []);

  function optionClickHandler(option, setOption, item, value) {
    setOption(value);
    localStorage.setItem(item, value);
  }

  return (
    <Container>
      <SettingsBlock>
        <Heading>BackSpace</Heading>
        <OptionBlock>
          <Option>disable</Option>
          <Option>limited</Option>
          <Option>necessary</Option>
          <Option>easy</Option>
        </OptionBlock>
      </SettingsBlock>
    </Container>
  );
}

export default Settings;

const Container = styled.div`
  width: 100%;
  min-height: calc(100vh - 140px);
  padding-top: 50px;
  display: flex;
  /* flex-wrap: wrap;
  justify-content: space-between; */
`;
// const TestMode = styled.div`
//   width: fit-content;
//   height: fit-content;
//   display: flex;
//   flex-direction: column;
//   flex-wrap: wrap;
// `;
const SettingsBlock = styled.div`

`
const Heading = withReveal(
  styled.span`
    font-size: 1.5rem;
    border-bottom: 1px solid cyan;
  `,
  <Fade bottom duration={300} />
);

const OptionBlock = styled.div`
  display: flex;
  /* flex-wrap: wrap; */
  flex-direction: column;
  /* align-items: center; */
  justify-content: space-between;
  padding: 20px 0 0 0;


  .selected-settings-option {
    background-color: #aaa;
    color: #1b1b1e;
  }
`;

const Option = withReveal(
  styled.span`
    font-size: 1rem;
    /* margin-right: 10px; */
    padding: 2px 7px;
    border: 1px solid #aaa;
    border-radius: 5px;
    cursor: pointer;
  `,
  <Fade top duration={300} />
);
