import React, { useEffect } from "react";
import styled from "styled-components";
import { Fade } from "react-reveal";
import HeadShake from "react-reveal/HeadShake";
import { useState } from "react";

function CustomTime(props) {
  const [time, setTime] = useState(
    localStorage.getItem("time") === 30 || localStorage.getItem("time") == 60
      ? { minutes: 0, seconds: 0 }
      : props.CalculateTime(localStorage.getItem("time"))
  );
  const [MinuteInputError, setMinuteInputError] = useState(false);
  const [SecondInputError, setSecondInputError] = useState(false);

  if (MinuteInputError || SecondInputError) {
    setTimeout(() => {
      setMinuteInputError(false);
      setSecondInputError(false);
    }, 100);
  }

  function InputError(t) {
    switch (t) {
      case "minutes":
        setMinuteInputError(true);
        break;
      case "seconds":
        setSecondInputError(true);
        break;
      default:
        break;
    }
  }

  function TimeHandler(a) {
    r = true;
    switch (a) {
      case "correction":
        if (time["minutes"] === 60) {
          setTime((prevTime) => ({ ...prevTime, ["seconds"]: 0 }));
          InputError(t);
        }
        break;
      case "permission":
        if (time["minutes"] === 60) {
          r = false;
        }
        break;

      default:
        break;
    }
    return r;
  }

  function valueController(m, l, v, t) {
    let r;
    if (v >= m && v <= l) {
      r = v;
      TimeHandler("correction");
    } else if (v > l) {
      r = l;
      InputError(t);
    } else if (v < m) {
      r = m;
      InputError(t);
    } else {
      r = 0;
    }
    return r;
  }

  function timeUpdate(e, t, c) {
    switch (c) {
      case "custom":
        setTime((prevTime) => ({
          ...prevTime,
          [t]: valueController(0, 60, e.target.value, t),
        }));
        break;
      case "increment":
        if (time[t] >= 0 && time[t] <= 59) {
          setTime((prevTime) => ({
            ...prevTime,
            [t]: parseInt(prevTime[t]) + 1,
          }));
          if (t == "minutes" && time[t] == 60) {
            setTime((prevTime) => ({ ...prevTime, seconds: 0 }));
          }
        } else {
          InputError(t);
        }
        break;
      case "decrement":
        if (time[t] >= 1 && time[t] <= 60) {
          setTime((prevTime) => ({
            ...prevTime,
            [t]: parseInt(prevTime[t]) - 1,
          }));
        } else {
          InputError(t);
        }
        break;
      case "reset":
        setTime((prevTime) => ({ ...prevTime, [t]: "" }));

        break;
      case "fill":
        setTime((prevTime) => ({ ...prevTime, [t]: 0 }));

        break;
      default:
        break;
    }
  }
  function keyDownHandler(event) {
    switch (event.key) {
      case "Escape":
        props.closeTimeInput();
        break;
      case "Enter":
        props.SubmitTime(
          parseInt(time["minutes"] * 60) + parseInt(time["seconds"])
        );
        break;

      default:
        break;
    }
  }
  return (
    <Container onKeyDown={(event) => keyDownHandler(event)} tabIndex="-1">
      <Fade bottom duration={250}>
        <TimeInput ref={props.timeMenuRef}>
          <CloseTimeInput
            src="icons/close.png"
            className="cyan-icon"
            onClick={() => props.closeTimeInput()}
          />
          <TimeInputUI>
            <MinuteBlock>
              <img
                src="/icons/up-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) => timeUpdate(event, "minutes", "increment")}
              />
              <HeadShake when={MinuteInputError} duration={400}>
                <MinuteInput
                  type="number"
                  value={time.minutes}
                  onFocus={
                    time.minutes == 0
                      ? (event) => timeUpdate(event, "minutes", "reset")
                      : (event) => timeUpdate(event, "minutes", "custom")
                  }
                  onBlur={
                    time.minutes == ""
                      ? (event) => timeUpdate(event, "minutes", "fill")
                      : (event) => timeUpdate(event, "minutes", "custom")
                  }
                  onChange={(event) => timeUpdate(event, "minutes", "custom")}
                />
              </HeadShake>
              <img
                src="/icons/down-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) => timeUpdate(event, "minutes", "decrement")}
              />
            </MinuteBlock>
            <span>m</span>
            <span className="time-input-divider">:</span>
            <SecondBlock>
              <img
                src="/icons/up-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) =>
                  time["minutes"] < 60 &&
                  timeUpdate(event, "seconds", "increment")
                }
              />
              <HeadShake when={SecondInputError} duration={400}>
                <SecondInput
                  type="number"
                  value={time.seconds}
                  onFocus={
                    time.seconds === 0
                      ? (event) => timeUpdate(event, "seconds", "reset")
                      : (event) => timeUpdate(event, "seconds", "custom")
                  }
                  onBlur={
                    time.seconds === ""
                      ? (event) => timeUpdate(event, "seconds", "fill")
                      : (event) => timeUpdate(event, "seconds", "custom")
                  }
                  onChange={(event) => timeUpdate(event, "seconds", "custom")}
                />
              </HeadShake>
              <img
                src="/icons/down-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) => timeUpdate(event, "seconds", "decrement")}
              />
            </SecondBlock>
            <span>s</span>
          </TimeInputUI>
          <Uploadtime
            src="/icons/tick-inside-circle.png"
            className="cyan-icon"
            onClick={() =>
              props.SubmitTime(
                parseInt(time["minutes"] * 60) + parseInt(time["seconds"])
              )
            }
          />
        </TimeInput>
      </Fade>
    </Container>
  );
}

export default CustomTime;

const Container = styled.div`
  position: absolute;
  right: 0;
  left: 0;
  top: 0;
  bottom: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  backdrop-filter: blur(3px);
  transition: all 250ms ease-in-out;
  z-index: 3;
`;
const TimeInput = styled.div`
  width: 300px;
  height: 150px;
  position: relative;
  background-color: #17171a;
  border-radius: 5px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  box-shadow: -1px 10px 23px 0px rgba(0, 0, 0, 0.51);
  -webkit-box-shadow: -1px 10px 23px 0px rgba(0, 0, 0, 0.51);
  -moz-box-shadow: -1px 10px 23px 0px rgba(0, 0, 0, 0.51);
`;

const CloseTimeInput = styled.img`
  width: 12px;
  position: absolute;
  right: 15px;
  top: 15px;
  cursor: pointer;
`;
const TimeInputUI = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  span {
    margin: 0 0 0 10px;
    font-size: 1.5em;
  }
  .arrow-icon {
    width: 22px;
  }
  .time-input-divider {
    margin: 0 10px;
    color: #00ffff99;
  }
  img {
    cursor: pointer;
  }
`;
const MinuteBlock = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  span {
    letter-spacing: normal;
    margin: 10px 0;
    color: #aaaaaa;
    cursor: pointer;
  }
  input::-webkit-outer-spin-button,
  input::-webkit-inner-spin-button {
    -webkit-appearance: none;
  }
  input[type="number"] {
    -moz-appearance: textfield;
  }
  input:focus {
    outline: none;
  }
`;

const MinuteInput = styled.input`
  background-color: transparent;
  border: none;
  color: #aaaaaa;
  font-size: 1.5em;
  text-align: center;
  width: 2ch;
`;

const SecondBlock = styled(MinuteBlock)``;

const SecondInput = styled(MinuteInput)``;

const Uploadtime = styled.img`
  width: 22px;
  cursor: pointer;
  opacity: 0.75;
  position: absolute;
  bottom: 15px;
  left: auto;
`;
