import React, { useState, useEffect } from "react";
import styled from "styled-components";

function TextInput(props) {
  const [currentWord, setCurrentWord] = useState();
  const [enteredValue, setEnteredValue] = useState();
  useEffect(() => setCurrentWord(document.getElementById("word-1")), []);

  function textInputEvaluater() {
    enteredValue = $("#textInput").val().replace(/\s/g, "");
    if (enteredValue != currentWord.slice(0, enteredValue.length)) {
      $("#" + word_count).removeClass("correct_warning");
      $("#" + word_count).addClass("wrong_warning");
      wrong = true;
    } else if (
      wrong &&
      currentWord.slice(0, enteredValue.length) == enteredValue
    ) {
      $("#" + word_count).removeClass("wrong_warning");
      wrong = false;
      if (enteredValue == currentWord) {
        $("#" + word_count).addClass("correct_warning");
      }
      current_word_blinker();
    } else if (enteredValue == currentWord) {
      $("#" + word_count).addClass("correct_warning");
    } else if (enteredValue.length < currentWord.length) {
      $("#" + word_count).removeClass("correct_warning");
      current_word_blinker();
    }
  }

  return <Container onChange={() => textInputEvaluater(e)}></Container>;
}

export default TextInput;

const Container = styled.input`
  position: absolute;
  top: 0;
  background-color: transparent;
  border: none;
  border-bottom: #aaa solid 2px;
  caret-color: #ffff;
  font-size: 1.2em;
  text-align: center;
  margin: 10px 0 0;
  padding: 3px 3px 5px;
  color: #ffff;
  width: ${(props) => props.width};

  &:focus {
    outline: none;
  }
`;
