const reducer = (
  state = { key: 0, id: "", word: "", width: 0, left: 0 },
  action
) => {
  switch (action.type) {
    case "NEXT_WORD":
      return loadWord(state, "NEXT");
    case "PREVIOUS_WORD":
      return loadWord(state, "PREVIOUS");
    case "CONFIRM_WORD":
      return loadWord(state, 1);
    default:
      return state;
  }
};

export default reducer;

function loadWord(state, type) {
  if (state.key == 0 ) {
    return {
      key: 1,
      id: "word-1",
      word: document.getElementById("word-1").innerText,
      width: document.getElementById("word-1").offsetWidth,
      left:
        document.getElementById("word-1").offsetLeft -
        document.getElementById("word-1").parentElement.offsetLeft,
    };
  } else {
    return {
      key: state.key + i,
      id: `word-${state.key + i}`,
      word: document.getElementById(`word-${state.key + i}`).innerText,
      width: document.getElementById(`word-${state.key + i}`).offsetWidth,
      left:
        document.getElementById(`word-${state.key + i}`).offsetLeft -
        document.getElementById(`word-${state.key + i}`).parentElement
          .offsetLeft,
    };
  }
}
