const reducer = (
  state = { word: "", isWrong: false, isComplete: false },
  action
) => {
  switch (action.type) {
    case "UPDATE_INPUT":
      return evaluateAndUpdateInput(
        action.payload.entered,
        action.payload.current
      );
    case "RESET_INPUT":
      return { word: "", isWrong: false, isComplete: false };
    default:
      return state;
  }
};

export default reducer;

function evaluateAndUpdateInput(passedInput, currentWord) {
  let isWrong = false;
  let isComplete = false;
  
}
