import React from 'react'
import styled from 'styled-components'

function Controls() {
  return (
    <Container>
    <Time>
      <TimeControls>
        <TimeSettings src="icons/timer_settings.png" className="time-icons grey-icon" />
        <TimeSettingsMenu>
          <Time30>30s</Time30>
          <Time60>60s</Time60>
          <TimeCustom src='icons/timer_input.png'/>
          <TimeDisable src='icons/stopwatch.png'/>
        </TimeSettingsMenu>
      </TimeControls>
      <TimeView></TimeView>
    </Time>
    <Restart src="icons/restart.png" className="grey-icon" />
  </Container>
  )
}

export default Controls

// controls
const Container = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-top: 40px;
  /* border: 1px solid grey; */
  .time-icons{
    width: 22px;
    height: 22px;
  }
`;
const Time = styled.div``;
const TimeControls = styled.div``;
const TimeSettings = styled.img``;
const TimeSettingsMenu = styled.div``;
const Time30 = styled.p``
const Time60 = styled.p``
