import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import { decrementTestTimer } from "../state/action-creators";

function CountdownTimer(props) {
  // states and functions for showing the countdown timer once the test starts

  // const time =
  //   localStorage.getItem("time") === null ? 30 : localStorage.getItem("time");

  const time = useSelector((state) => state.testTimer);
  const dispatch = useDispatch();

  const [timerTime, setTimerTime] = useState(null);

  function startTimer() {
    setInterval(function () {
      // minutes = parseInt(timer / 60, 10);
      // seconds = parseInt(timer % 60, 10);

      // minutes = minutes < 10 ? "0" + minutes : minutes;
      // seconds = seconds < 10 ? "0" + seconds : seconds;

      // if (minutes > "00") {
      //   r = minutes + ":" + seconds;
      // } else {
      //   r = seconds;
      // }
      // if (--timer < 0) {
      //   timer = duration;
      // }
      // setTimerTime(r);
      dispatch(decrementTestTimer());
    }, 1000);
  }

  useEffect(() => {
    if (props.testOn && time > 0) {
      startTimer();
    }
  }, [props.testOn]);

  function displayTime(time) {
    let r;
    let minutes = parseInt(time / 60, 10);
    let seconds = parseInt(time % 60, 10);

    minutes = minutes < 10 ? "0" + minutes : minutes;
    seconds = seconds < 10 ? "0" + seconds : seconds;

    if (minutes > "00") {
      r = minutes + ":" + seconds;
    } else {
      r = seconds;
    }
    return r;
  }

  return (
    <TimeView>
      <p id="time-view">{displayTime(time)}</p>
    </TimeView>
  );
}

export default CountdownTimer;

const TimeView = styled.div``;
