import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import { Slide, Fade } from "react-reveal";
import CustomTime from "./CustomTime";
import CountdownTimer from "./CountdownTimer";
import { useDispatch } from "react-redux";
import { resetTestTimer } from "../state/action-creators";

function Controls(props) {
  // states and state managers

  const timeMenuRef = useRef(null);

  const [time, setTime] = useState(
    localStorage.getItem("time") === null ? 30 : localStorage.getItem("time")
  );
  const dispatch = useDispatch();

  const [customTimeInput, setCustomTimeInput] = useState(false);
  const [showTimeMenu, setShowTimeMenu] = useState(false);

  function calculateTime(sec, t) {
    let r;
    if (sec >= 0) {
      var m = Math.floor((sec) / 60);
      var s = Math.floor((sec) % 60);
    } else {
      var m = Math.ceil((sec % 3600) / 60);
      var s = Math.ceil((sec % 3600) % 60);
    }
    var mDisplay = m < 10 ? `0${m}` : m;
    var sDisplay = s < 10 ? `0${s}` : s;
    switch (t) {
      case "for-display":
        r = { minutes: mDisplay, seconds: sDisplay };
        break;
      case "for-state":
        r = { minutes: m, seconds: s };
        break;
      default:
        break;
    }
    return r;
  }

  function timerSettingsClick() {
    setShowTimeMenu((prevShowTimeMenu) => !prevShowTimeMenu);
  }

  function closeTimeInput() {
    setCustomTimeInput(false);
  }

  const timeOptionClickHandler = (value) => {
    localStorage.setItem("time", value);
    setTime(localStorage.getItem("time"));
    dispatch(resetTestTimer());
    setShowTimeMenu(false);
    setCustomTimeInput(false);
  };

  // function to close opened time-option-menu when the user clicks outside

  function handleClickOutside(e) {
    if (
      timeMenuRef.current &&
      !timeMenuRef.current.contains(e.target) &&
      !e.target.matches("#custom-time-input, #custom-time-input *")
    ) {
      setShowTimeMenu(false);
      closeTimeInput();
    }
  }

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside, true);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside, true);
    };
  }, [timeMenuRef]);

  return (
    <Container>
      <Fade left duration={250}>
        <Time>
          {props.testOn && time > 0 ? (
            <CountdownTimer testOn={props.testOn} />
          ) : (
            <TimeControls ref={timeMenuRef}>
              <TimeSettings
                src="icons/timer_settings.png"
                className="time-icons grey-icon"
                onClick={(event) => {
                  event.stopPropagation();
                  timerSettingsClick(event);
                }}
              />

              <TimeSettingsMenuWrap>
                <Slide
                  duration={300}
                  delay={showTimeMenu ? 100 : 0}
                  left
                  when={showTimeMenu}
                  className="time-settings-menu"
                >
                  <TimeSettingsMenu className={!showTimeMenu && "display-none"}>
                    <Time0
                      onClick={() => timeOptionClickHandler(0)}
                      className={time == 0 ? "selected-time-option" : ""}
                    >
                      0s
                    </Time0>

                    <Time30
                      onClick={() => timeOptionClickHandler(30)}
                      className={time == 30 ? "selected-time-option" : ""}
                    >
                      30s
                    </Time30>

                    <Time60
                      onClick={() => timeOptionClickHandler(60)}
                      className={time == 60 ? "selected-time-option" : ""}
                    >
                      60s
                    </Time60>

                    <TimeCustom
                      src="icons/time_input.png"
                      onClick={() => setCustomTimeInput(true)}
                      className={
                        time != 0 && time != 30 && time != 60
                          ? "cyan-icon"
                          : "grey-icon"
                      }
                    />
                  </TimeSettingsMenu>
                </Slide>
              </TimeSettingsMenuWrap>

              <CurrentTimeWrap>
                <Slide
                  duration={!showTimeMenu ? 250 : 100}
                  delay={!showTimeMenu ? 300 : 0}
                  left
                  when={!showTimeMenu}
                >
                  <CurrentTime
                    className={showTimeMenu && "display-none"}
                    duration={!showTimeMenu ? 250 : 100}
                    delay={!showTimeMenu ? 300 : 0}
                    left={!showTimeMenu}
                    showTimeMenu={showTimeMenu}
                  >
                    {time <= 60
                      ? `${time}s`
                      : `${calculateTime(time, "for-display").minutes}:${
                          calculateTime(time, "for-display").seconds
                        }`}
                  </CurrentTime>
                </Slide>
              </CurrentTimeWrap>
            </TimeControls>
          )}
        </Time>
      </Fade>

      <Fade right duration={250}>
        <Restart src="icons/restart.png" className="grey-icon" />
      </Fade>

      {customTimeInput && (
        <CustomTime
          calculateTime={calculateTime}
          submitInput={timeOptionClickHandler}
          closeTimeInput={closeTimeInput}
          timeMenuRef={timeMenuRef}
        />
      )}
    </Container>
  );
}

export default Controls;

// controls

const Container = styled.div`
  padding-top: 35px;

  display: flex;
  justify-content: space-between;
  align-items: center;

  #time-view {
    margin: 0;
    color: cyan;
  }

  .time-icons {
    width: 22px;
    height: 22px;
  }

  @media (max-width: 430px) {
    padding-top: 15px;
    font-size: 0.75em;

    .time-icons {
      width: 18px;
      height: 18px;
    }
  }
`;

const Time = styled.div`
  display: flex;
  align-items: center;
`;

const TimeControls = styled.div`
  display: flex;
  align-items: center;

  position: relative;
`;

const TimeSettings = styled.img`
  cursor: pointer;
`;

const TimeSettingsMenuWrap = styled.div`
  position: absolute;
  left: 27px;
  z-index: 2;
  background-color: transparent;

  overflow: hidden;

  display: flex;

  * {
    background-color: transparent;
  }
`;

const CurrentTimeWrap = styled(TimeSettingsMenuWrap)`
  cursor: pointer;
`;

const TimeSettingsMenu = styled.div`
  display: flex;
  align-items: center;

  * {
    margin: 0 5px;
    cursor: pointer;
  }

  .selected-time-option {
    color: cyan;
  }
`;

const TimeCustom = styled.img`
  width: 19px;
  height: 19px;

  @media (max-width: 430px) {
    width: 15px;
    height: 15px;
  }
`;

const CurrentTime = styled.div`
  border-bottom: 1px solid cyan;
  box-sizing: border-box;
  margin-bottom: 5px;
  z-index: 1;

  display: flex;
  align-items: center;
  justify-content: center;
`;

const Restart = styled.img`
  width: 24px;
  cursor: pointer;

  @media (max-width: 430px) {
    width: 20px;
  }
`;

const Time30 = styled.p``;

const Time60 = styled.p``;

const Time0 = styled.p``;
