import { store } from "../store";
import words from "../../components/wordsData";
import { prevWord } from "../action-creators";

const reducer = (state = [], action) => {
  switch (action.type) {
    case "NEW_WORDS":
      return testWordsLoader(action.payload.inputType);
    case "EVALUATE_WORDS":
      return testWordsEvaluate(
        state,
        action.payload.currentWord,
        action.payload.enteredWord
      );
    case "UPDATE_CURRENT_WORD":
      return updateCurrentWord(
        state,
        action.payload.direction,
        action.payload.currentWord,
        action.payload.enteredWord
      );
    default:
      return state;
  }
};

export default reducer;

function currentWordAssigner(i, inputType) {
  if (i == 0 && inputType == "FLOAT") {
    return "current-word current-word-blinker";
  } else {
    return "";
  }
}

function testWordsLoader(inputType) {
  let j = 500;
  let wordList = [];
  let prevRanNum = 0;
  for (let i = 0; i < j; i++) {
    var r = Math.floor(Math.random() * words.length);
    if (r == prevRanNum) {
      r++;
    }
    prevRanNum = r;
    const word = words[r];
    let letterArray = [];
    for (let l = 0; l < word.length; l++) {
      letterArray.push(
        <span
          key={`word-${i + 1}-letter-${l + 1}`}
          id={`word-${i + 1}-letter-${l + 1}`}
          className={`letter${
            i == 0 && inputType == "INLINE"
              ? " current-letter"
              : ""
          }`}
        >
          {word[l]}
        </span>
      );
    }
    wordList.push(
      <span
        className={`test-words${
          i == 0 && inputType == "FLOAT"
            ? " current-word current-word-blinker"
            : ""
        }`}
        id={`word-${i + 1}`}
        key={`word-${i + 1}`}
      >
        {letterArray}
      </span>
    );
    wordList.push(" ");
  }
  return wordList;
}

function testWordsEvaluate(prevTestWords, currentWord, enteredWord) {
  let enteredWordLength = enteredWord.length;
  let currentWordLength = currentWord.word.length;
  let currentWordSliced = currentWord.word.slice(0, enteredWordLength);
  let currentWordIndex = currentWord.index;
  let currentWordId = currentWord.id;
  let updatedWord;
  let updatedWordList;
  if (enteredWordLength <= currentWordLength) {
    if (enteredWord != currentWordSliced) {
      if (enteredWord != "") {
        updatedWord = wordElementUpdater(
          currentWord,
          currentWordLength,
          enteredWord,
          "WRONG_WARNING",
          "FLOAT"
        );
      } else {
        updatedWord = wordElementUpdater(
          currentWord,
          currentWordLength,
          enteredWord,
          "EMPTY",
          "FLOAT"
        );
      }
    } else if (enteredWord == currentWordSliced) {
      if (enteredWordLength < currentWordLength) {
        if (enteredWord != "") {
          updatedWord = wordElementUpdater(
            currentWord,
            currentWordLength,
            enteredWord,
            "CORRECT",
            "FLOAT"
          );
        } else {
          updatedWord = wordElementUpdater(
            currentWord,
            currentWordLength,
            enteredWord,
            "EMPTY",
            "FLOAT"
          );
        }
      } else if (enteredWordLength == currentWordLength) {
        updatedWord = wordElementUpdater(
          currentWord,
          currentWordLength,
          enteredWord,
          "COMPLETED_WARNING",
          "FLOAT"
        );
      }
    }
  } else if (enteredWordLength > currentWordLength) {
    updatedWord = wordElementUpdater(
      currentWord,
      currentWordLength,
      enteredWord,
      "WRONG_WARNING",
      "FLOAT"
    );
  }
  if (updatedWord) {
    let updatedWordList = [...prevTestWords];
    if (currentWordIndex % 2 == 0 || currentWordIndex == 0) {
      updatedWordList[currentWordIndex] = updatedWord;
    } else {
      updatedWordList[currentWordIndex + 1] = updatedWord;
    }
    return updatedWordList;
  } else {
    return prevTestWords;
  }
}

function updateCurrentWord(prevTestWords, direction, currentWord, enteredWord) {
  let currentWordIndex = currentWord.index;
  let currentWordLength = currentWord.word.length;

  let updatedWord;

  if (enteredWord == currentWord.word) {
    updatedWord = wordElementUpdater(
      currentWord,
      currentWordLength,
      enteredWord,
      "CORRECT",
      "FINISHED_WORD"
    );
  } else {
    updatedWord = wordElementUpdater(
      currentWord,
      currentWordLength,
      enteredWord,
      "WRONG",
      "FINISHED_WORD"
    );
  }

  let updatedWordList = [...prevTestWords];
  if (currentWordIndex % 2 == 0 || currentWordIndex == 0) {
    updatedWordList[currentWordIndex] = updatedWord;
  } else {
    updatedWordList[currentWordIndex + 1] = updatedWord;
  }
  return updatedWordList;
}

function wordElementUpdater(
  currentWord,
  currentWordLength,
  enteredWord,
  wordState,
  inputType
) {
  let letterArray = [];
  let updatedWord;
  switch (inputType) {
    case "FLOAT":
      for (let l = 0; l < currentWordLength; l++) {
        letterArray.push(
          <span
            key={`word-${currentWord.key}-letter-${l + 1}`}
            id={`word-${currentWord.key}-letter-${l + 1}`}
            className="letter"
          >
            {currentWord.word[l]}
          </span>
        );
      }
      switch (wordState) {
        case "WRONG_WARNING":
          updatedWord = (
            <span
              className={"test-words current-word wrong-warning"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        case "COMPLETED_WARNING":
          updatedWord = (
            <span
              className={"test-words current-word completed-warning"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        case "CORRECT":
          updatedWord = (
            <span
              className={"test-words current-word"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        case "EMPTY":
          updatedWord = (
            <span
              className={"test-words current-word current-word-blinker"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        default:
          return updatedWord;
      }
    case "INLINE":
      return updatedWord;
    case "FINISHED_WORD":
      switch (wordState) {
        case "CORRECT":
          for (let l = 0; l < currentWordLength; l++) {
            letterArray.push(
              <span
                key={`word-${currentWord.key}-letter-${l + 1}`}
                id={`word-${currentWord.key}-letter-${l + 1}`}
                className="letter"
              >
                {currentWord.word[l]}
              </span>
            );
          }
          updatedWord = (
            <span
              className={"test-words correct"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        case "WRONG":
          for (let l = 0; l < currentWordLength; l++) {
            letterArray.push(
              <span
                key={`word-${currentWord.key}-letter-${l + 1}`}
                id={`word-${currentWord.key}-letter-${l + 1}`}
                className={`letter ${
                  currentWord.word[l] == enteredWord[l] ? "yellow" : "wrong"
                }`}
              >
                {currentWord.word[l]}
              </span>
            );
          }
          updatedWord = (
            <span
              className={"test-words"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        default:
          return updatedWord;
      }
  }
}
