import React, { useEffect, useState } from "react";
import styled from "styled-components";
import words from "./wordsData";
import TextInput from "./TextInput";

function Test(props) {
  const [wordsRendered,setWordsRendered] = useState(false)
  function testWordsLoader() {
    let j = 500;
    let wordList = [];
    let prevRanNum = 0;
    for (let i = 0; i < j; i++) {
      var r = Math.floor(Math.random() * words.length);
      if (r == prevRanNum) {
        r++;
      }
      const word = words[r];
      let letterArray = [];
      for (let l = 0; l < word.length; l++) {
        letterArray.push(
          <span key={`word-${i + 1}-letter-${l + 1}`} className="letter">
            {word[l]}
          </span>
        );
      }
      wordList.push(
        <span
          className={`test-words${i == 0 ? " current-word" : ""}`}
          id={`word-${i + 1}`}
          key={`word-${i + 1}`}
        >
          {letterArray}
        </span>
      );
