import { store } from "../store";
import words from "../../components/wordsData";

const reducer = (state = [], action) => {
  switch (action.type) {
    case "NEW_WORDS":
      return testWordsLoader();
    case "EVALUATE_WORDS":
      return testWordsUpdate(
        state,
        action.payload.currentWord,
        action.payload.enteredWord
      );
    default:
      return state;
  }
};

export default reducer;

function testWordsLoader() {
  let j = 500;
  let wordList = [];
  let prevRanNum = 0;
  for (let i = 0; i < j; i++) {
    var r = Math.floor(Math.random() * words.length);
    if (r == prevRanNum) {
      r++;
    }
    prevRanNum = r;
    const word = words[r];
    let letterArray = [];
    for (let l = 0; l < word.length; l++) {
      letterArray.push(
        <span
          key={`word-${i + 1}-letter-${l + 1}`}
          id={`word-${i + 1}-letter-${l + 1}`}
          className="letter"
        >
          {word[l]}
        </span>
      );
    }
    wordList.push(
      <span
        className={`test-words${i == 0 ? " current-word" : ""}`}
        id={`word-${i + 1}`}
        key={`word-${i + 1}`}
      >
        {letterArray}
      </span>
    );
    wordList.push(" ");
  }
  return wordList;
}

function testWordsUpdate(prevTestWords, currentWord, enteredWord) {
  let enteredWordLength = enteredWord.length;
  let currentWordLength = currentWord.word.length;
  let currentWordSliced = currentWord.word.slice(0, enteredWordLength);
  let currentWordIndex = currentWord.key - 1;
  let updatedWord;
  console.log(enteredWord);
  if (enteredWordLength <= currentWord.length) {
    if (enteredWord != currentWordSliced) {
      console.log(":)");
      let letterArray = [];
      for (let l = 0; l < currentWordLength; l++) {
        letterArray.push(
          <span
            key={`word-${currentWord.key}-letter-${l + 1}`}
            id={`word-${currentWord.key}-letter-${l + 1}`}
            className="letter"
          >
            {currentWord.word[l]}
          </span>
        );
      }
      updatedWord = (
        <span
          className={"test-words current-word wrong-warning"}
          id={currentWord.id}
          key={currentWord.key}
        >
          {letterArray}
        </span>
      );
    }
    console.log(prevTestWords[currentWordIndex]);
    console.log(updatedWord);
  }
  return prevTestWords;
}
