import React, { useEffect, useState, useRef, forwardRef } from "react";
import styled from "styled-components";
import words from "./wordsData";
import TextInput from "./TextInput";
import { Fade } from "react-reveal";
import { useSelector, useDispatch } from "react-redux";
import {
  newWords,
  evaluateWords,
  blurInput,
  focusInput,
  confirmWord,
} from "../state/action-creators/index";

function Test(props) {
  const dispatch = useDispatch();
  const [wordsRendered, setWordsRendered] = useState(false);
  const testWords = useSelector((state) => state.testWords);
  const currentWord = useSelector((state) => state.currentWord);
  const inputFocused = useSelector((state) => state.inputFocused);
  const enteredWord = useSelector((state) => state.enteredWord);

  function loadWords() {
    dispatch(newWords());
    setWordsRendered(true);
  }

  useEffect(() => {
    loadWords();
  }, []);

  useEffect(() => {
    if (currentWord.key != 0) {
      dispatch(confirmWord(currentWord));
    }
  }, [testWords]);

  const testRef = useRef(null);
  function handleClickOutside(e) {
    if (
      (testRef.current && !testRef.current.contains(e.target)) ||
      e.target.matches("#textInput")
    ) {
      dispatch(blurInput());
    } else {
      dispatch(focusInput());
    }
  }

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside, true);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside, true);
    };
  }, [testRef]);

  return (
    <Container>
      <Fade bottom duration={250}>
        {wordsRendered ? <TextInput /> : ""}
        <TestContent id="testContent">
          <Underline
            width={`${currentWord.width}px`}
            left={`${currentWord.left}px`}
            enteredWordIsWrong={enteredWord.isWrong}
            enteredWordIsComplete={enteredWord.isCom}
          ></Underline>

          <div id="testContentChild" ref={testRef}>
            {testWords}
          </div>
        </TestContent>
      </Fade>
    </Container>
  );
}
export default Test;

const Container = styled.div`
  padding-top: 30px;
`;

const TestContent = styled.div`
  height: 80px;
  overflow: hidden;
  font-size: 1.3em;
  font-family: "Quicksand", sans-serif;
  font-weight: 500;
  line-height: 40px;
  text-align: justify;
  #testContentChild {
    transition: all 250ms ease-in-out;
  }
`;

const Underline = styled.div`
  position: absolute;
  width: ${(props) => props.width};
  height: 36px;
  padding: 0;
  transform: translateX(${(props) => props.left});
  transition: all 150ms ease-in-out;
  border-bottom: #aaa 2px solid;
  border-color: ${(props) =>
    props.enteredWordIsWrong
      ? "red"
      : props.enteredWordIsComplete
      ? "00ffff99"
      : "#aaa"};
`;
