import React, { useEffect, useState } from "react";
import styled from "styled-components";
import words from "./wordsData";
import TextInput from "./TextInput";
import { Fade } from "react-reveal";
import { useSelector, useDispatch } from "react-redux";
import { newWords, evaluateWords } from "../state/action-creators/index";

function Test(props) {
  const dispatch = useDispatch();
  const [wordsRendered, setWordsRendered] = useState(false);
  const testWords = useSelector((state) => state.testWords);
  const currentWord = useSelector((state) => state.currentWord);
  localStorage.setItem("input", "INLINE");
  const inputType =
    localStorage.getItem("input") === null
      ? "FLOAT"
      : localStorage.getItem("input");

  function loadWords() {
    dispatch(newWords(inputType));
    setWordsRendered(true);
  }

  useEffect(() => {
    loadWords();
  }, []);
  return (
    <Container inputType={inputType}>
      <Fade bottom duration={250}>
        {wordsRendered ? <TextInput /> : ""}
        <TestContent id="testContent">
          <Underline
            width={`${currentWord.width}px`}
            left={`${currentWord.left}px`}
          ></Underline>
          <div id="testContentChild">{testWords}</div>
        </TestContent>
      </Fade>
    </Container>
  );
}
console.log(currentWord.width);
export default Test;

const Container = styled.div`
  padding-top: ${(props) => (props.inputType == "INLINE" ? "30px" : "0px")};
`;

const TestContent = styled.div`
  height: 80px;
  overflow: hidden;
  font-size: 1.3em;
  font-family: "Quicksand", sans-serif;
  font-weight: 500;
  line-height: 40px;
  text-align: justify;
  #testContentChild {
    transition: all 250ms ease-in-out;
  }

  .test-words {
    padding: 0px 3px 5px 0px;
    margin: 0px 5px 20px 0px;
  }

  .current-word {
    background-color: #aaa;
    color: #1b1b1e;
    border-radius: 3px;
  }
`;

const Underline = styled.div`
  position: absolute;
  width: ${(props) => props.width};
  height: 38px;
  padding: 0;
  transform: translateX(${(props) => props.left});
  transition: all 150ms ease-in-out;
  border-bottom: #aaa 2px solid;
`;
