import words from "../../components/wordsData";

const reducer = (state = [], action) => {
  switch (action.type) {
    case "NEW_WORDS":
      return testWordsLoader();
    case "UPDATE_CURRENT_WORD":
      return updateCurrentWord(
        state,
        action.payload.currentWord,
        action.payload.enteredWord
      );
    case "SKIP_CURRENT_WORD":
      return skipCurrentWord(
        state,
        action.payload.currentWord,
        action.payload.enteredWord,
        action.payload.skipTo
      );
    default:
      return state;
  }
};

export default reducer;

function testWordsLoader() {
  let j = 500;
  let wordList = [];
  let prevRanNum = 0;
  for (let i = 0; i < j; i++) {
    var r = Math.floor(Math.random() * words.length);
    if (r == prevRanNum) {
      r++;
    }
    prevRanNum = r;
    const word = words[r];
    let letterArray = [];
    for (let l = 0; l < word.length; l++) {
      letterArray.push(
        <span
          key={`word-${i + 1}-letter-${l + 1}`}
          id={`word-${i + 1}-letter-${l + 1}`}
          className={`letter${i == 0 && l == 0 ? " current-letter" : ""}`}
        >
          {word[l]}
        </span>
      );
    }
    wordList.push(
      <span
        className={`test-words${i == 0 ? " current-word" : ""}`}
        id={`word-${i + 1}`}
        key={`word-${i + 1}`}
      >
        {letterArray}
      </span>
    );
    wordList.push(" ");
  }
  return wordList;
}

function updateCurrentWord(prevTestWords, currentWord, enteredWord) {
  let updatedWordList = [];
  let currentWordIndex = currentWord.index;

  let updatedWord = wordElementUpdater(currentWord, enteredWord);

  updatedWordList = [...prevTestWords];
  updatedWordList[currentWordIndex] = updatedWord;
  return updatedWordList;
}

function skipCurrentWord(prevTestWords, currentWord, enteredWord, skipTo) {
  let updatedWordList = [];
  let currentWordIndex = currentWord.index;
  switch (skipTo) {
    case "FORWARD":
      let updatedWord = wordElementUpdater(currentWord, enteredWord);

      updatedWordList = [...prevTestWords];
      updatedWordList[currentWordIndex] = updatedWord;
      return updatedWordList;
    default:
      break;
  }
}

function wordElementUpdater(currentWord, enteredWord) {
  let enteredWordLength = enteredWord.word.length;
  let currentWordLength = currentWord.word.length;

  let letterArray = [];
  let updatedWord;

  if (currentWordLength >= enteredWordLength) {
    for (let l = 0; l < currentWordLength; l++) {
      letterArray.push(
        <span
          key={`word-${currentWord.key}-letter-${l + 1}`}
          id={`word-${currentWord.key}-letter-${l + 1}`}
          className={`letter ${
            l + 1 <= enteredWordLength
              ? currentWord.word[l] == enteredWord.word[l]
                ? classValues.correct
                : classValues.wrong
              : l == enteredWordLength
              ? " current-letter"
              : ""
          }`}
        >
          {currentWord.word[l]}
        </span>
      );
    }
  } else {
    for (let l = 0; l < enteredWordLength; l++) {
      letterArray.push(
        <span
          key={`word-${currentWord.key}-letter-${l + 1}`}
          id={`word-${currentWord.key}-letter-${l + 1}`}
          className={`letter ${
            l + 1 <= enteredWordLength
              ? currentWord.word[l] == enteredWord.word[l]
                ? classValues.correct
                : classValues.wrong
              : l == enteredWordLength
              ? " current-letter"
              : ""
          }`}
        >
          {l + 1 < currentWordLength ? currentWord.word[l] : enteredWord[l]}
        </span>
      );
    }
  }
  updatedWord = (
    <span className={"test-words"} id={currentWord.id} key={currentWord.id}>
      {letterArray}
    </span>
  );
  return updatedWord;
}
