import React, { useEffect } from "react";
import styled from "styled-components";
import { Fade } from "react-reveal";
import HeadShake from "react-reveal/HeadShake";
import { useState } from "react";

function CustomTime(props) {
  const [time, setTime] = useState(
    localStorage.getItem("time") == 30 || localStorage.getItem("time") == 60
      ? { minutes: 0, seconds: 0 }
      : props.CalculateTime(localStorage.getItem("time"))
  );
  const [MinuteInputError, setMinuteInputError] = useState(false);
  const [SecondInputError, setSecondInputError] = useState(false);
  if (MinuteInputError||SecondInputError) {
    setTimeout(() => {
      setMinuteInputError(false);
      setSecondInputError(false);
    }, 400);
  }

  

  function valueController(m, l, v,t) {
    let r;
    if (v >= m && v <= l) {
      r = v;
    } else if (v > l) {
      r = l;
      switch (t) {
        case "minitue":
          setMinuteInputError(true)
          break;
        case "seconds":
          setSecondInputError(true)
          break
        default:
          break;
      }
    } else if (v < m) {
      r = m;
      switch (t) {
        case "minitue":
          setMinuteInputError(true)
          break;
        case "seconds":
          setSecondInputError(true)
          break
        default:
          break;
      }
    } else {
      r = 0;
    }
    return r;
  }

  function timeUpdate(e, t, c) {
    switch (c) {
      case "custom":
        setTime((prevTime) => ({
          ...prevTime,
          [t]: valueController(0, 60, e.target.value),
        }));
        setInputError(false);
        break;
      case "increment":
        if (time[t] >= 0 && time[t] <= 59) {
          setTime((prevTime) => ({
            ...prevTime,
            [t]: parseInt(prevTime[t]) + 1,
          }));
        } else {
          setInputError(true);
        }
        break;
      case "decrement":
        if (time[t] >= 1 && time[t] <= 60) {
          setTime((prevTime) => ({
            ...prevTime,
            [t]: parseInt(prevTime[t]) - 1,
          }));
        } else {
          setInputError(true);
        }
        break;
      case "reset":
        setTime((prevTime) => ({ ...prevTime, [t]: "" }));

        break;
      case "fill":
        setTime((prevTime) => ({ ...prevTime, [t]: 0 }));

        break;
      default:
        break;
    }
  }
  return (
    <Container>
      <Fade bottom duration={250}>
        <TimeInput ref={props.timeMenuRef}>
          <CloseTimeInput
            id="close-time-input"
            src="icons/close.png"
            className="cyan-icon"
          />
          <TimeInputUI>
            <MinuteBlock>
              <img
                src="/icons/up-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) => timeUpdate(event, "minutes", "increment")}
              />
              <HeadShake when={inputError} duration={400}>
                <MinuteInput
                  type="number"
                  value={time.minutes}
                  onFocus={
                    time.minutes == 0
                      ? (event) => timeUpdate(event, "minutes", "reset")
                      : (event) => timeUpdate(event, "minutes", "custom")
                  }
                  onBlur={
                    time.minutes == ""
                      ? (event) => timeUpdate(event, "minutes", "fill")
                      : (event) => timeUpdate(event, "minutes", "custom")
                  }
                  onChange={(event) => timeUpdate(event, "minutes", "custom")}
                />
              </HeadShake>
              <img
                src="/icons/down-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) => timeUpdate(event, "minutes", "decrement")}
              />
            </MinuteBlock>
            <span>m</span>
            <span className="time-input-divider">:</span>
            <SecondBlock>
              <img
                src="/icons/up-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) =>
                  !time["minutes"] == 60 &&
                  timeUpdate(event, "seconds", "increment")
                }
              />
              <SecondInput
                type="number"
                value={time.seconds}
                onFocus={
                  time.seconds == 0
                    ? (event) => timeUpdate(event, "seconds", "reset")
                    : (event) => timeUpdate(event, "seconds", "custom")
                }
                onBlur={
                  time.seconds == ""
                    ? (event) => timeUpdate(event, "seconds", "fill")
                    : (event) => timeUpdate(event, "seconds", "custom")
                }
                onChange={(event) => timeUpdate(event, "seconds", "custom")}
              />
              <img
                src="/icons/down-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
                onClick={(event) => timeUpdate(event, "seconds", "decrement")}
              />
            </SecondBlock>
            <span>s</span>
          </TimeInputUI>
          <Uploadtime
            src="/icons/tick-inside-circle.png"
            className="cyan-icon"
            onClick={() =>
              props.SubmitTime(time["minutes"] * 60 + time["seconds"])
            }
          />
        </TimeInput>
      </Fade>
    </Container>
  );
}

export default CustomTime;

const Container = styled.div`
  position: absolute;
  right: 0;
  left: 0;
  top: 0;
  bottom: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  backdrop-filter: blur(3px);
  transition: all 250ms ease-in-out;
  z-index: 3;
`;
const TimeInput = styled.div`
  width: 300px;
  height: 150px;
  background-color: white;
  position: relative;
  background-color: #0f0f11;
  border-radius: 5px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const CloseTimeInput = styled.img`
  width: 12px;
  position: absolute;
  right: 15px;
  top: 15px;
  cursor: pointer;
`;
const TimeInputUI = styled.div`
  width: 100%;
  /* margin-top: 20px; */
  display: flex;
  justify-content: center;
  align-items: center;
  span {
    margin: 0 0 0 10px;
    font-size: 1.5em;
  }
  .arrow-icon {
    width: 22px;
  }
  .time-input-divider {
    margin: 0 10px;
    color: #00ffff99;
  }
  img {
    cursor: pointer;
  }
`;
const MinuteBlock = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  span {
    letter-spacing: normal;
    margin: 10px 0;
    color: #aaaaaa;
    cursor: pointer;
  }
  input::-webkit-outer-spin-button,
  input::-webkit-inner-spin-button {
    -webkit-appearance: none;
  }
  input[type="number"] {
    -moz-appearance: textfield;
  }
  input:focus {
    outline: none;
  }
`;

const MinuteInput = styled.input`
  background-color: transparent;
  border: none;
  color: #aaaaaa;
  font-size: 1.5em;
  text-align: center;
  width: 2ch;
`;

const SecondBlock = styled(MinuteBlock)``;

const SecondInput = styled(MinuteInput)``;

const Uploadtime = styled.img`
  width: 22px;
  cursor: pointer;
`;
