const reducer = (
  state = {
    time:
      localStorage.getItem("time") === null
        ? 30
        : parseInt(localStorage.getItem("time")),
    liveTime:
      localStorage.getItem("time") === null
        ? 30
        : parseInt(localStorage.getItem("time")),
    calculatedTime: {
      forDisplay: calculateTime(
        localStorage.getItem("time") === null
          ? 30
          : parseInt(localStorage.getItem("time")),
        "for-display"
      ),
      forState: calculateTime(
        localStorage.getItem("time") === null
          ? 30
          : parseInt(localStorage.getItem("time")),
        "for-state"
      ),
    },
  },
  action
) => {
  switch (action.type) {
    case "RESET_LIVE_TIME":
      return { ...state, liveTime: state.time };
    case "DECREMENT_LIVE_TIME":
      return { ...state, liveTime: state.liveTime - 1 };
    case "UPDATE_TEST_TIMER":
      localStorage.setItem("time", action.payload);
      return {
        time:
          localStorage.getItem("time") === null
            ? 30
            : parseInt(localStorage.getItem("time")),
        liveTime:
          localStorage.getItem("time") === null
            ? 30
            : parseInt(localStorage.getItem("time")),
      };
    default:
      return state;
  }
};

function calculateTime(sec, t) {
  let r;
  if (sec >= 0) {
    var m = Math.floor(sec / 60);
    var s = Math.floor(sec % 60);
  } else {
    var m = Math.ceil((sec % 3600) / 60);
    var s = Math.ceil((sec % 3600) % 60);
  }
  var mDisplay = m < 10 ? `0${m}` : m;
  var sDisplay = s < 10 ? `0${s}` : s;
  switch (t) {
    case "for-display":
      r = { minutes: mDisplay, seconds: sDisplay };
      break;
    case "for-state":
      r = { minutes: m, seconds: s };
      break;
    default:
      break;
  }
  return r;
}

export default reducer;
