import { store } from "../store";
import words from "../../components/wordsData";
import { prevWord } from "../action-creators";

const reducer = (state = [], action) => {
  switch (action.type) {
    case "NEW_WORDS":
      return testWordsLoader();
    case "EVALUATE_WORDS":
      return testWordsUpdate(
        state,
        action.payload.currentWord,
        action.payload.enteredWord
      );
    case "UPDATE_CURRENT_WORD":
      return updateCurrentWord(
        state,
        action.payload.direction,
        action.payload.currentWord,
        action.payload.enteredWord
      );
    default:
      return state;
  }
};

export default reducer;

function testWordsLoader() {
  let j = 500;
  let wordList = [];
  let prevRanNum = 0;
  for (let i = 0; i < j; i++) {
    var r = Math.floor(Math.random() * words.length);
    if (r == prevRanNum) {
      r++;
    }
    prevRanNum = r;
    const word = words[r];
    let letterArray = [];
    for (let l = 0; l < word.length; l++) {
      letterArray.push(
        <span
          key={`word-${i + 1}-letter-${l + 1}`}
          id={`word-${i + 1}-letter-${l + 1}`}
          className="letter"
        >
          {word[l]}
        </span>
      );
    }
    wordList.push(
      <span
        className={`test-words${
          i == 0 ? " current-word current-word-blinker" : ""
        }`}
        id={`word-${i + 1}`}
        key={`word-${i + 1}`}
      >
        {letterArray}
      </span>
    );
    wordList.push(" ");
  }
  return wordList;
}

function testWordsUpdate(prevTestWords, currentWord, enteredWord) {
  let enteredWordLength = enteredWord.length;
  let currentWordLength = currentWord.word.length;
  let currentWordSliced = currentWord.word.slice(0, enteredWordLength);
  let currentWordIndex = currentWord.key - 1;
  let currentWordId = currentWord.id;
  let updatedWord;
  let updatedWordList;
  if (enteredWordLength <= currentWordLength) {
    if (enteredWord != currentWordSliced) {
      if (enteredWord != "") {
        updatedWord = wordElementUpdater(
          currentWord,
          currentWordLength,
          "WRONG_WARNING",
          "FLOAT"
        );
      } else {
        updatedWord = wordElementUpdater(
          currentWord,
          currentWordLength,
          "EMPTY",
          "FLOAT"
        );
      }
    } else if (enteredWord == currentWordSliced) {
      if (enteredWordLength < currentWordLength) {
        if (enteredWord != "") {
          updatedWord = wordElementUpdater(
            currentWord,
            currentWordLength,
            "CORRECT",
            "FLOAT"
          );
        } else {
          updatedWord = wordElementUpdater(
            currentWord,
            currentWordLength,
            "EMPTY",
            "FLOAT"
          );
        }
      } else if (enteredWordLength == currentWordLength) {
        updatedWord = wordElementUpdater(
          currentWord,
          currentWordLength,
          "COMPLETED_WARNING",
          "FLOAT"
        );
      }
    }
  } else if (enteredWordLength > currentWordLength) {
    updatedWord = wordElementUpdater(
      currentWord,
      currentWordLength,
      "WRONG_WARNING",
      "FLOAT"
    );
  }
  if (updatedWord) {
    let firstHalf = prev
    updatedWordList = [];
    // updatedWordList.splice(currentWordIndex, 1);
    // updatedWordList.splice(currentWordIndex, 0, updatedWord);
    updatedWordList[currentWordIndex] = updatedWord;
    return updatedWordList;
  } else {
    return prevTestWords;
  }
}

function updateCurrentWord(prevTestWords, direction, currentWord, enteredWord) {
  prevWord = document.getElementById(`word-${currentWord.key - 1}`).innerText;
  switch (direction) {
    case "FORWARD":
      if (enteredWord == prevWord) {
      }
    default:
      return prevTestWords;
  }
}

function wordElementUpdater(
  currentWord,
  currentWordLength,
  wordState,
  inputType
) {
  switch (inputType) {
    case "FLOAT":
      let letterArray = [];
      let updatedWord;
      for (let l = 0; l < currentWordLength; l++) {
        letterArray.push(
          <span
            key={`word-${currentWord.key}-letter-${l + 1}`}
            id={`word-${currentWord.key}-letter-${l + 1}`}
            className="letter"
          >
            {currentWord.word[l]}
          </span>
        );
      }
      switch (wordState) {
        case "WRONG_WARNING":
          updatedWord = (
            <span
              className={"test-words current-word wrong-warning"}
              id={currentWord.id}
              key={currentWord.key}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        case "COMPLETED_WARNING":
          updatedWord = (
            <span
              className={"test-words current-word completed-warning"}
              id={currentWord.id}
              key={currentWord.key}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        case "CORRECT":
          updatedWord = (
            <span
              className={"test-words current-word"}
              id={currentWord.id}
              key={currentWord.key}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        case "EMPTY":
          updatedWord = (
            <span
              className={"test-words current-word current-word-blinker"}
              id={currentWord.id}
              key={currentWord.key}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        default:
          return updatedWord;
      }
  }
}
