const reducer = (
  state = { timeInput: false, timeMenu: false },
  action
) => {
  switch (action.type) {
    case "SHOW_TIME_INPUT":
      return { ...state, customTimeInput: true };
    case "CLOSE_TIME_INPUT":
      return { ...state, customTimeInput: false };
    case "SHOW_TIME_MENU":
      return { ...state, showTimeMenu: true };
    case "CLOSE__TIME_MENU":
      return { ...state, showTimeMenu: false };
    default:
      return state;
  }
};

export default reducer;
