    }, 1000);
  }

  useEffect(() => {
    if (liveTime < 0) {
      dispatch(resetLiveTime());
    }
  }, [liveTime]);

  useEffect(() => {
    if (testOn && time > 0) {
      runTimer();
    }
  }, [testOn]);

  return (
    <TimeView>
      <p id="time-view">
        {time <= 60
          ? `${time}s`
          : `${timeForDisplay.minutes}:${timeForDisplay.seconds}`}
      </p>
    </TimeView>
  );
}

export default CountdownTimer;

