import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import {
  firstWord,
  nextWord,
  prevWord,
  updateInput,
  resetInput,
  newWords,
  evaluateWords,
  updateCurrentWordForward,
  focusInput,
  blurInput,
} from "../state/action-creators/index";

function TextInput(props) {
  const currentWord = useSelector((state) => state.currentWord);
  const enteredWord = useSelector((state) => state.enteredWord);
  const testWords = useSelector((state) => state.testWords);
  const inputFocused = useSelector((state) => state.inputFocused);
  const dispatch = useDispatch();
  const inputType =
    localStorage.getItem("input") === null
      ? "FLOAT"
      : localStorage.getItem("input");

  const testContentScrollerCount = useRef(1);

  var textInput = document.getElementById("textInput");

  useEffect(() => {
    if (currentWord.key > 0) {
      if (currentWord.key > 1) {
        dispatch(resetInput());
        if (
          document.getElementById(currentWord.id).offsetLeft -
            document.getElementById(currentWord.id).parentElement.offsetLeft ==
          0
        ) {
          testContentScroller();
        }
      }
    }
  }, [currentWord]);

  useEffect(() => {
    dispatch(firstWord());
  }, []);

  useEffect(() => {
    if (currentWord.key != 0) {
      dispatch(evaluateWords(enteredWord, currentWord, inputType));
    }
  }, [enteredWord]);

  function testContentScroller() {
    let currentLineHeight = parseInt(
      window
        .getComputedStyle(document.getElementById("testContent"))
        .getPropertyValue("line-height")
    );
    document.getElementById(
      "testContentChild"
    ).style.transform = `translateY(-${
      currentLineHeight * testContentScrollerCount.current
    }px)`;
    testContentScrollerCount.current++;
  }

  function textInputChangeHandler(e) {
    let keyClicked = e.nativeEvent.data;
    let enteredValue = String(e.target.value.replace(/\s/g, ""));
    // if (keyClicked == " ") {
    //   dispatch(updateCurrentWordForward(enteredWord, currentWord, inputType));
    //   dispatch(nextWord());
    // } else {
    //   dispatch(updateInput(enteredValue));
    // }
    dispatch(updateInput(enteredValue));
  }

  function keyDownHandler(e) {
    let keyClicked = e.nativeEvent.code;
    let enteredValue = String(e.target.value.replace(/\s/g, ""));
    if (keyClicked == "Space" && enteredValue != "") {
      dispatch(updateCurrentWordForward(enteredWord, currentWord, inputType));
      dispatch(nextWord());
    }
  }

  function inputOnBlur() {
    console.log('1');
    if (inputFocused && currentWord.key > 0) {
      dispatch(blurInput());
    }
  }
  function inputOnFocus() {
    console.log('2');
    if (!inputFocused && currentWord.key > 0) {
      dispatch(focusInput());
    }
  }

  return (
    <Container
      width={`${currentWord.width}px`}
      left={`${currentWord.left}px`}
      inputType={inputType}
    >
      <input
        id="textInput"
        autoCapitalize="off"
        autoComplete="off"
        spellCheck="false"
        maxLength="15"
        value={enteredWord}
        onKeyDown={(e) => keyDownHandler(e)}
        onChange={(e) => setTimeout(textInputChangeHandler(e), 1)}
        onClick={log}
        // onBlur={inputOnBlur()}
        // onFocus={inputOnFocus()}
      ></input>
    </Container>
  );
}

export default TextInput;

const Container = styled.div`
  width: 100%;
  position: ${(props) =>
    props.inputType == "INLINE" ? "absolute" : "relative"};
  input {
    background-color: transparent;
    border: none;
    border-bottom: ${(props) =>
      props.inputType == "INLINE" ? "none" : "#aaa solid 2px"};
    caret-color: ${(props) =>
      props.inputType == "INLINE" ? "transparent" : "#ffff"};
    font-size: 1.2em;
    font-family: "Quicksand", sans-serif;
    font-weight: 500;
    text-align: center;
    margin: 10px 0 20px;
    padding: 3px 3px 5px;
    color: ${(props) =>
      props.inputType == "INLINE" ? "transparent" : "#ffff"};
    width: ${(props) => props.width};
    transform: translateX(${(props) => props.left});
    transition: all 150ms ease-in-out;

    &:focus {
      outline: none;
    }
  }
`;
