import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import { decrementLiveTime } from "../state/action-creators";

function CountdownTimer(props) {
  // states and functions for showing the countdown timer once the test starts

  const time = useSelector((state) => state.testTimer.liveTime);
  const dispatch = useDispatch();

  function runTimer() {
    setInterval(function () {
      dispatch(decrementLiveTime());
    }, 1000);
  }

  function displayTime(time) {
    let r;
    let minutes = parseInt(time / 60, 10);
    let seconds = parseInt(time % 60, 10);

    minutes = minutes < 10 ? "0" + minutes : minutes;
    seconds = seconds < 10 ? "0" + seconds : seconds;

    if (minutes > "00") {
      r = minutes + ":" + seconds;
    } else {
      r = seconds;
    }
    return r;
  }

  useEffect(() => {
    if (time < 0) {
      dispatch(reset)
    }
  }, [time]);

  useEffect(() => {
    if (props.testOn && time > 0) {
      runTimer();
    }
  }, [props.testOn]);

  return (
    <TimeView>
      <p id="time-view">{displayTime(time)}</p>
    </TimeView>
  );
}

export default CountdownTimer;

const TimeView = styled.div``;
