import React, { useState, useEffect, useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import styled from "styled-components";
import Controls from "./Controls";
import Test from "./Test";
import TestResult from "./TestResult";

function Home() {
  const showResult = useSelector((state) => state.showResult);
  const testOn = useSelector((state) => state.testOn);

  return (
    <Container id="HomeSection">
      <Controls />
      <DiaologBox testOn={testOn}>
        shift/ctrl + space/enter - restart
      </DiaologBox>
      {showResult ? <TestResult /> : <Test />}
    </Container>
  );
}

export default Home;

const Container = styled.div`
  min-height: calc(100vh - 150px);
  position: relative;
  /* border: 1px solid white; */

  @media (max-width: 639px) {
    min-height: calc(100vh - 180px);
  }

  &:focus {
    outline: none;
  }
`;

const DiaologBox = styled.div`
  position: absolute;
  width: 100%;
  display: flex;
  /* padding: 60px 0 0; */
  /* border: 1px solid; */
  justify-content: center;
  color: ${(props)=>props.testOn ? "transparent":"rgba(255, 255, 255, 0.2)"}
  font-size: 1rem;
`;
