import React, { useState, useEffect } from "react";
import styled from "styled-components";
import withReveal from "react-reveal/withReveal";
import Fade from "react-reveal/Fade";
import Pulse from "react-reveal/Pulse";
import {
  resetInput,
  resetLiveTime,
  resetTest,
  resetWord,
} from "../state/action-creators";
import { useDispatch } from "react-redux";

function Settings() {
  const dispatch = useDispatch();
  useEffect(() => {
    resetTest(dispatch);
  }, []);

  function optionClickHandler(option, setOption, item, value) {
    setOption(value);
    localStorage.setItem(item, value);
  }

  return <Container>
    <OptionBlock>
      <Heading>BackSpace</Heading>
      <Option>disable</Option>
      <Option></Option>
    </OptionBlock>
  </Container>;
}

export default Settings;

const Container = styled.div`
  width: 100%;
  min-height: calc(100vh - 140px);
  padding-top: 50px;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
`;
// const TestMode = styled.div`
//   width: fit-content;
//   height: fit-content;
//   display: flex;
//   flex-direction: column;
//   flex-wrap: wrap;
// `;

const Heading = withReveal(
  styled.span`
    font-size: 1.5rem;
    border-bottom: 1px solid cyan;
  `,
  <Fade bottom duration={300} />
);

const OptionBlock = styled.div`
  display: flex;
  justify-content: center;
  padding: 20px 0 0 0;
  width: 250px;
  height: fit-content;
  padding: 10px;
  border: solid rgba(170,170,170,0.2);

  .selected-settings-option {
    background-color: #aaa;
    color: #1b1b1e;
  }
`;

const Option = withReveal(
  styled.span`
    font-size: 1.5rem;
    margin-right: 10px;
    padding: 3px 7px;
    border: 1px solid #aaa;
    border-radius: 5px;
    cursor: pointer;
  `,
  <Fade top duration={300} />
);
