import React from "react";
import styled from "styled-components";

function Controls() {
  return (
    <Container>
      <Time>
        <TimeControls>
          <TimeSettings
            src="icons/timer_settings.png"
            className="time-icons grey-icon"
          />
          <TimeSettingsMenu>
            <Time30>30s</Time30>
            <Time60>60s</Time60>
            <TimeDisable
              src="icons/disable_timer.png"
              className="grey-icon time-icons"
            />
            <TimeCustom
              src="icons/time_input.png"
              className="grey-icon time-icons"
            />
          </TimeSettingsMenu>
          
        </TimeControls>
        <TimeView></TimeView>
      </Time>
      <Restart src="icons/restart.png" className="grey-icon" />
    </Container>
  );
}

export default Controls;

// controls
const Container = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-top: 40px;
  .time-icons {
    width: 22px;
    height: 22px;
  }
`;
const Time = styled.div``;
const TimeControls = styled.div`
  display: flex;
`;
const TimeSettings = styled.img`
    margin-right: 5px;  
`;
const TimeSettingsMenu = styled.div`
  display: flex;
  *{
      margin:0 5px;
  }
`;
const Time30 = styled.p``;
const Time60 = styled.p``;
const TimeCustom = styled.img``;
const TimeDisable = styled.img``;
const TimeView = styled.div``;

const Restart = styled.img`
  width: 24px;
`;
