import words from "../../components/wordsData";

const reducer = (state = [], action) => {
  switch (action.type) {
    case "NEW_WORDS":
      return testWordsLoader();
    case "UPDATE_CURRENT_WORD":
      return updateCurrentWord(
        state,
        action.payload.currentWord,
        action.payload.enteredWord,
        action.payload.isFinalUpdate,
        action.payload.blinkCurrentLetter
      );
    case "INACTIVATE_CURRENT_WORD":
      return updateCurrentWord(
        state,
        action.payload.currentWord,
        action.payload.enteredWord,
        action.payload.isFinalUpdate,
        action.payload.blinkCurrentLetter
      );
    default:
      return state;
  }
};

export default reducer;

function testWordsLoader() {
  let j = 500;
  let wordList = [];
  let prevRanNum = 0;
  for (let i = 0; i < j; i++) {
    var r = Math.floor(Math.random() * words.length);
    if (r == prevRanNum) {
      r++;
    }
    prevRanNum = r;
    const word = words[r];
    let letterArray = [];
    for (let l = 0; l < word.length; l++) {
      letterArray.push(
        <span
          key={`word-${i + 1}-letter-${l + 1}`}
          id={`word-${i + 1}-letter-${l + 1}`}
          className={`letter${i == 0 && l == 0 ? " current-letter" : ""}`}
        >
          {word[l]}
        </span>
      );
    }
    wordList.push(
      <span
        className={`test-words${i == 0 ? " current-word" : ""}`}
        id={`word-${i + 1}`}
        key={`word-${i + 1}`}
      >
        {letterArray}
      </span>
    );
    wordList.push(" ");
  }
  return wordList;
}

function updateCurrentWord(
  prevTestWords,
  currentWord,
  enteredWord,
  isFinalUpdate,
  blinkCurrentLetter
) {
  let updatedWordList = [];
  let currentWordIndex = currentWord.index;

  let enteredWordLength = enteredWord.word.length;
  let currentWordLength = currentWord.word.length;

  let letterArray = [];
  let updatedWord;

  if (currentWordLength >= enteredWordLength) {
    for (let l = 0; l < currentWordLength; l++) {
      letterArray.push(
        <span
          key={`word-${currentWord.key}-letter-${l + 1}`}
          id={`word-${currentWord.key}-letter-${l + 1}`}
          // className={`letter ${
          //   l + 1 <= enteredWordLength
          //     ? currentWord.word[l] == enteredWord.word[l]
          //       ? enteredWord.isWrong ||
          //         (isFinalUpdate && enteredWordLength < currentWordLength)
          //         ? "custom-dark-yellow"
          //         : "custom-dark-cyan"
          //       : "custom-dark-red"
          //     : l == enteredWordLength && !isFinalUpdate && blinkCurrentLetter
          //     ? " current-letter"
          //     : isFinalUpdate
          //     ? "custom-dark-red"
          //     : ""
          // }`}
          className={`letter ${classAssigner(
            currentWord[l],
            enteredWord[l],
            enteredWordLength,
            l,
            enteredWord.isWrong,
            isFinalUpdate,
            blinkCurrentLetter
          )}`}
        >
          {currentWord.word[l]}
        </span>
      );
    }
  } else {
    for (let l = 0; l < enteredWordLength; l++) {
      letterArray.push(
        <span
          key={`word-${currentWord.key}-letter-${l + 1}`}
          id={`word-${currentWord.key}-letter-${l + 1}`}
          className={`letter ${
            l + 1 <= enteredWordLength
              ? currentWord.word[l] == enteredWord.word[l]
                ? enteredWord.isWrong
                  ? "custom-dark-yellow"
                  : "custom-dark-cyan"
                : "custom-dark-red"
              : l == enteredWordLength && !isFinalUpdate
              ? " current-letter"
              : ""
          }`}
        >
          {l + 1 < currentWordLength
            ? currentWord.word[l]
            : enteredWord.word[l]}
        </span>
      );
    }
  }
  updatedWord = (
    <span className={"test-words"} id={currentWord.id} key={currentWord.id}>
      {letterArray}
    </span>
  );

  updatedWordList = [...prevTestWords];
  updatedWordList[currentWordIndex] = updatedWord;
  return updatedWordList;
}

function classAssigner(
  currentLetter,
  enteredLetter,
  enteredWordLength,
  currentLetterIndex,
  enteredWordIsWrong,
  isFinalUpdate,
  blinkCurrenntLetter
) {
  let classVal = "letter "
  if(currentLetter == enteredLetter){
    if(enteredWordIsWrong){
      classVal= classVal+"custom-dark-yellow"
    }else{
      classVal=+"custom-dark-cyan"
    }
  }else{
    classVal+"custom-dark-red"
  }
  return classVal
}

function inactivateCurrentWord(prevTestWords, currentWord, enteredWord) {
  let currentLetterIndex = enteredWord.word.length;
  let currentWordToUpdate = prevTestWords[currentWord.index];
  let currentLetter = currentWordToUpdate.props.children[currentLetterIndex];
  let updatedCurrentLetterProps = {
    ...currentLetter.props,
    className: "letter",
  };
  let updatedCurrentLetter = {
    ...currentLetter,
    props: updatedCurrentLetterProps,
  };
  let updatedWord = currentWordToUpdate;
  updatedWord.props.children[currentLetterIndex] = updatedCurrentLetter;
  let updatedWordList = [...prevTestWords];
  updatedWordList[currentWord.index] = updatedWord;
  return updatedWordList;
}
