export const firstWord = () => {
  return { type: "FIRST_WORD" };
};
export const confirmWord = (currentWord) => {
  return { type: "CONFIRM_WORD", payload: currentWord };
};

export const resetWord = () => {
  return { type: "RESET_WORD" };
};

export const nextWord = () => {
  return { type: "NEXT_WORD" };
};

export const prevWord = () => {
  return { type: "PREVIOUS_WORD" };
};

export const updateInput = (value) => {
  return { type: "UPDATE_INPUT", payload: {} };
};

export const resetInput = () => {
  return { type: "RESET_INPUT", payload: "" };
};

export const newWords = (inputType) => {
  return { type: "NEW_WORDS", payload: { inputType } };
};

export const evaluateWords = (enteredWord, currentWord, inputType) => {
  return {
    type: "EVALUATE_WORDS",
    payload: { enteredWord, currentWord, inputType },
  };
};

export const updateCurrentWordForward = (
  enteredWord,
  currentWord,
  inputType
) => {
  return {
    type: "UPDATE_CURRENT_WORD",
    payload: { direction: "FORWARD", enteredWord, currentWord, inputType },
  };
};

export const focusInput = () => {
  return {
    type: "FOCUS_INPUT",
  };
};
export const blurInput = () => {
  return {
    type: "BLUR_INPUT",
  };
};
