import { store as states } from "../store";
import words from "../../components/wordsData";


const reducer = (state = [], action) => {
  switch (action.type) {
    case "NEW_WORDS":
      testWordsLoader();
    case "EVALUATE_WORDS":
      return testWordsUpdate(state);
    default:
      return state;
  }
};

export default reducer;

function testWordsLoader() {
  let j = 500;
  let wordList = [];
  let prevRanNum = 0;
  for (let i = 0; i < j; i++) {
    var r = Math.floor(Math.random() * words.length);
    if (r == prevRanNum) {
      r++;
      console.log("corrected");
    }
    prevRanNum = r;
    const word = words[r];
    let letterArray = [];
    for (let l = 0; l < word.length; l++) {
      letterArray.push(
        <span
          key={`word-${i + 1}-letter-${l + 1}`}
          id={`word-${i + 1}-letter-${l + 1}`}
          className="letter"
        >
          {word[l]}
        </span>
      );
    }
    wordList.push(
      <span
        className={`test-words${i == 0 ? " current-word" : ""}`}
        id={`word-${i + 1}`}
        key={`word-${i + 1}`}
      >
        {letterArray}
      </span>
    );
    wordList.push(" ");
  }
  return wordList;
}

function testWordsUpdate(prevTestWords) {
  let currentWordSliced = states.currentWord.word.slice(0, states.enteredWord);
  let currentWordIndex = states.currentWord.key - 1;
  let updatedWord = "";
  if (states.enteredWord != states.currentWord) {
    console.log("wrong for" + prevTestWords[currentWordIndex]);
  }
}
