import words from "../../components/wordsData";

const reducer = (state = [], action) => {
  switch (action.type) {
    case "NEW_WORDS":
      return testWordsLoader();
    case "EVALUATE_WORDS":
      return testWordsEvaluate(
        state,
        action.payload.currentWord,
        action.payload.enteredWord,
        action.payload.inputType
      );
    // case "UPDATE_CURRENT_WORD":
    //   return updateCurrentWord(
    //     state,
    //     action.payload.direction,
    //     action.payload.currentWord,
    //     action.payload.enteredWord
    //   );
    case "UPDATE_CURRENT_WORD":
      return updateCurrentWord(
        state,
        action.payload.currentWord,
        action.payload.enteredWord
      );
    case "SKIP_CURRENT_WORD":
      return skipCurrentWord(
        state,
        action.payload.currentWord,
        action.payload.enteredWord,
        action.payload.skipTo
      );
    default:
      return state;
  }
};

export default reducer;

function testWordsLoader() {
  let j = 500;
  let wordList = [];
  let prevRanNum = 0;
  for (let i = 0; i < j; i++) {
    var r = Math.floor(Math.random() * words.length);
    if (r == prevRanNum) {
      r++;
    }
    prevRanNum = r;
    const word = words[r];
    let letterArray = [];
    for (let l = 0; l < word.length; l++) {
      letterArray.push(
        <span
          key={`word-${i + 1}-letter-${l + 1}`}
          id={`word-${i + 1}-letter-${l + 1}`}
          className={`letter${i == 0 && l == 0 ? " current-letter" : ""}`}
        >
          {word[l]}
        </span>
      );
    }
    wordList.push(
      <span
        className={`test-words${i == 0 ? " current-word" : ""}`}
        id={`word-${i + 1}`}
        key={`word-${i + 1}`}
      >
        {letterArray}
      </span>
    );
    wordList.push(" ");
  }
  return wordList;
}

function updateCurrentWord(prevTestWords, currentWord, enteredWord) {
  enteredWordLength = enteredWord.word.length;
  letterArray = [];
  for (i = 0; i < enteredWordLength; i++) {
    letterArray.push(
      <span
        id={`${currentWord.id}-letter-${i + 1}`}
        key={`${currentWord.id}-letter-${i + 1}`}
        className={
          "letter" + enteredWord[i] == currentWord[i] ? " correct" : " wrong"
        }
      ></span>
    );
  }
}

function testWordsEvaluate(prevTestWords, currentWord, enteredWord, inputType) {
  let enteredWordLength = enteredWord.length;
  let currentWordLength = currentWord.word.length;
  let currentWordSliced = currentWord.word.slice(0, enteredWordLength);
  let currentWordIndex = currentWord.index;
  let updatedWord;
  let updatedWordList;
  if (enteredWordLength <= currentWordLength) {
    if (enteredWord != currentWordSliced) {
      if (enteredWord != "") {
        updatedWord = wordElementUpdater(
          currentWord,
          currentWordLength,
          enteredWord,
          "WRONG_WARNING",
          inputType
        );
      } else {
        updatedWord = wordElementUpdater(
          currentWord,
          currentWordLength,
          enteredWord,
          "EMPTY",
          inputType
        );
      }
    } else if (enteredWord == currentWordSliced) {
      if (enteredWordLength < currentWordLength) {
        if (enteredWord != "") {
          updatedWord = wordElementUpdater(
            currentWord,
            currentWordLength,
            enteredWord,
            "CORRECT",
            inputType
          );
        } else {
          updatedWord = wordElementUpdater(
            currentWord,
            currentWordLength,
            enteredWord,
            "EMPTY",
            inputType
          );
        }
      } else if (enteredWordLength == currentWordLength) {
        updatedWord = wordElementUpdater(
          currentWord,
          currentWordLength,
          enteredWord,
          "COMPLETED_WARNING",
          inputType
        );
      }
    }
  } else if (enteredWordLength > currentWordLength) {
    updatedWord = wordElementUpdater(
      currentWord,
      currentWordLength,
      enteredWord,
      "WRONG_WARNING",
      inputType
    );
  }
  if (updatedWord) {
    updatedWordList = [...prevTestWords];
    updatedWordList[currentWordIndex] = updatedWord;
    return updatedWordList;
  } else {
    return prevTestWords;
  }
}

