function wordElementUpdater(
  currentWord,
  currentWordLength,
  enteredWord,
  wordState,
  inputType
) {
  let letterArray = [];
  let updatedWord;
  switch (inputType) {
    case "FLOAT":
      for (let l = 0; l < currentWordLength; l++) {
        letterArray.push(
          <span
            key={`word-${currentWord.key}-letter-${l + 1}`}
            id={`word-${currentWord.key}-letter-${l + 1}`}
            className="letter"
          >
            {currentWord.word[l]}
          </span>
        );
      }
      switch (wordState) {
        case "WRONG_WARNING":
          updatedWord = (
            <span
              className={"test-words current-word wrong-warning"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        case "COMPLETED_WARNING":
          updatedWord = (
            <span
              className={"test-words current-word completed-warning"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        case "CORRECT":
          updatedWord = (
            <span
              className={"test-words current-word"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        case "EMPTY":
          updatedWord = (
            <span
              className={"test-words current-word current-word-blinker"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        default:
          return updatedWord;
      }
    case "INLINE":
      let enteredWordLength = enteredWord.length;
      if (currentWordLength >= enteredWordLength) {
        for (let l = 0; l < currentWordLength; l++) {
          letterArray.push(
            <span
              key={`word-${currentWord.key}-letter-${l + 1}`}
              id={`word-${currentWord.key}-letter-${l + 1}`}
              className={`letter${
                l + 1 <= enteredWordLength
                  ? currentWord.word[l] == enteredWord[l]
                    ? " correct"
                    : " wrong"
                  : l == enteredWordLength
                  ? " current-letter"
                  : ""
              }`}
            >
              {currentWord.word[l]}
            </span>
          );
        }
      } else {
        for (let l = 0; l < enteredWordLength; l++) {
          letterArray.push(
            <span
              key={`word-${currentWord.key}-letter-${l + 1}`}
              id={`word-${currentWord.key}-letter-${l + 1}`}
              className={`letter${
                l + 1 <= enteredWordLength
                  ? currentWord.word[l] == enteredWord[l]
                    ? " correct"
                    : " wrong"
                  : l == enteredWordLength
                  ? " current-letter"
                  : ""
              }`}
            >
              {l + 1 < currentWordLength ? currentWord.word[l] : enteredWord[l]}
            </span>
          );
        }
      }
      updatedWord = (
        <span className={"test-words"} id={currentWord.id} key={currentWord.id}>
          {letterArray}
        </span>
      );
      return updatedWord;
    case "FINISHED_WORD":
      switch (wordState) {
        case "CORRECT":
          for (let l = 0; l < currentWordLength; l++) {
            letterArray.push(
              <span
                key={`word-${currentWord.key}-letter-${l + 1}`}
                id={`word-${currentWord.key}-letter-${l + 1}`}
                className="letter"
              >
                {currentWord.word[l]}
              </span>
            );
          }
          updatedWord = (
            <span
              className={"test-words correct"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        case "WRONG":
          let loopCount =
            currentWordLength >= enteredWord.length
              ? currentWordLength
              : enteredWord.length;
          for (let l = 0; l < loopCount; l++) {
            letterArray.push(
              <span
                key={`word-${currentWord.key}-letter-${l + 1}`}
                id={`word-${currentWord.key}-letter-${l + 1}`}
                className={`letter ${
                  currentWord.word[l] == enteredWord[l] ? "yellow" : "wrong"
                }`}
              >
                {l + 1 <= currentWordLength
                  ? currentWord.word[l]
                  : enteredWord[l]}
              </span>
            );
          }
          updatedWord = (
            <span
              className={"test-words"}
              id={currentWord.id}
              key={currentWord.id}
            >
              {letterArray}
            </span>
          );
          return updatedWord;
        default:
          return updatedWord;
      }
  }
}
