import React, { useState, useEffect, useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import styled from "styled-components";
import Controls from "./Controls";
import Test from "./Test";
import TestResult from "./TestResult";
import {MobileView} from "react-device-detect"

function Home() {
  const showResult = useSelector((state) => state.showResult);
  const testOn = useSelector((state) => state.testOn);
  const inputFocused = useSelector((state) => state.inputFocused);

  return (
    <Container id="HomeSection">
      <Controls />
      {showResult ? <TestResult /> : <Test />}
      <MobileView>
        
      </MobileView>
      <DiaologBox
        testOn={testOn}
        inputFocused={inputFocused}
        showResult={showResult}
      >
        shift/ctrl + space/enter - restart
      </DiaologBox>
    </Container>
  );
}

export default Home;

const Container = styled.div`
  min-height: calc(100vh - 150px);
  position: relative;
  /* border: 1px solid white; */

  @media (max-width: 639px) {
    min-height: calc(100vh - 180px);
  }

  &:focus {
    outline: none;
  }
`;

const DiaologBox = styled.div`
  position: absolute;
  width: 100%;
  display: flex;
  /* padding: 30px 0 0; */
  bottom: 30px;
  /* border: 1px solid; */
  justify-content: center;
  color: ${(props) =>
    props.testOn && props.inputFocused && !props.showResult
      ? "transparent"
      : "rgba(255, 255, 255, 0.15)"};
  font-size: 1rem;
  transition: color 200ms ease-in-out;
`;
