import React from "react";
import styled from "styled-components";
import { Fade } from "react-reveal";
import { useState } from "react";

function CustomTime() {
  return (
    <Container>
      <Fade bottom duration={250}>
        <TimeInput>
          <CloseTimeInput src="icons/close.png" className="cyan-icon" />
          <TimeInputUI>
            <MinuteBlock>
              <img
                src="/icons/up-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
              />
              <MinuteInput type="number" min="0" value="00"/>
              <img
                src="/icons/down-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
              />
            </MinuteBlock>
            <span>m</span>
            <span className="time-input-divider">:</span>
            <SecondBlock>
              <img
                src="/icons/up-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
              />
              <span>00</span>
              <img
                src="/icons/down-arrow.png"
                className="grey-icon arrow-icon"
                alt=""
              />
            </SecondBlock>
            <span>s</span>
          </TimeInputUI>
        </TimeInput>
      </Fade>
    </Container>
  );
}

export default CustomTime;

const Container = styled.div`
  position: absolute;
  right: 0;
  left: 0;
  top: 0;
  bottom: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  backdrop-filter: blur(3px);
  z-index: 3;
`;
const TimeInput = styled.div`
  width: 300px;
  height: 150px;
  background-color: white;
  position: relative;
  background-color: #0f0f11;
  border-radius: 5px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const CloseTimeInput = styled.img`
  width: 12px;
  position: absolute;
  right: 15px;
  top: 15px;
  cursor: pointer;
`;
const TimeInputUI = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  span {
    margin: 0 0 0 10px;
    font-size: 1.5em;
  }
  .arrow-icon {
    width: 22px;
  }
  .time-input-divider {
    margin: 0 10px;
    color: #00ffff99;
  }
  img {
    cursor: pointer;
  }
`;
const MinuteBlock = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  span {
    letter-spacing: normal;
    margin: 10px 0;
    color: #aaaaaa;
    cursor: pointer;
  }
`;

const MinuteInput = styled.input`

`

const SecondBlock = styled(MinuteBlock)``;
