import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { styled as styledMUI } from "@mui/material/styles";
import { alpha } from '@mui/material/styles';
import { pink } from '@mui/material/colors';
import withReveal from "react-reveal/withReveal";
import Fade from "react-reveal/Fade";
import Pulse from "react-reveal/Pulse";
import { Switch } from "@mui/material";
import { FormControlLabel } from "@mui/material";
import {
  resetInput,
  resetLiveTime,
  resetTest,
  resetWord,
} from "../state/action-creators";
import { useDispatch } from "react-redux";

function Settings() {
  const dispatch = useDispatch();
  useEffect(() => {
    resetTest(dispatch);
  }, []);

  const settingsData = { backSpaceOn: true };

  function optionClickHandler(option, setOption, item, value) {
    setOption(value);
    localStorage.setItem(item, value);
  }

  const Android12Switch = styledMUI(Switch)(({ theme }) => ({
    padding: 8,
    "& .MuiSwitch-track": {
      // backgroundColor: pink[600] ,
      borderRadius: 22 / 2,
      color: '#1b1b1e',
      "&:before, &:after": {
        content: '""',
        position: "absolute",
        top: "50%",
        transform: "translateY(-50%)",
        width: 20,
        height: 20,
      },
      // "&:hover": {
      //   backgroundColor: alpha(pink[600], theme.palette.action.hoverOpacity),
      // },
      // "&:before": {
      //   backgroundImage: `url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" height="16" width="16" viewBox="0 0 24 24"><path fill="${encodeURIComponent(
      //     theme.palette.getContrastText(theme.palette.primary.main)
      //   )}" d="M21,7L9,19L3.5,13.5L4.91,12.09L9,16.17L19.59,5.59L21,7Z"/></svg>')`,
      //   left: 12,
      // },
      // "&:after": {
      //   backgroundImage: `url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" height="16" width="16" viewBox="0 0 24 24"><path fill="${encodeURIComponent(
      //     theme.palette.getContrastText(theme.palette.primary.main)
      //   )}" d="M19,13H5V11H19V13Z" /></svg>')`,
      //   right: 12,
      // },
    },
    '& .MuiSwitch-switchBase.Mui-checked': {
      backgroundColor: "#aaa",
      color: "#fff"
    },
    "& .MuiSwitch-thumb": {
      boxShadow: "none",
      width: 16,
      height: 16,
      margin: 2,
    },
  }));

  return (
    <Container>
      <SettingsBlock>
        <MainSettingsBlockChild>
          <MainHeading>BackSpace</MainHeading>
          {/* <SettingsToggleOption>
            {settingsData.backSpaceOn ? "enabled" : "disabled"}
            
          </SettingsToggleOption> */}
          {/* <Switch /> */}
          <FormControlLabel control={<Android12Switch defaultChecked />} />
        </MainSettingsBlockChild>
      </SettingsBlock>
    </Container>
  );
}

export default Settings;

const Container = styled.div`
  width: 100%;
  min-height: calc(100vh - 140px);
  padding-top: 50px;
`;
const SettingsBlock = styled.div`
  border: 1px solid rgba(170, 170, 170, 0.3);
  padding: 10px;
  box-sizing: border-box;
  width: 40%;
`;

const MainSettingsBlockChild = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  box-sizing: border-box;
  /* border-bottom: 1px solid rgba(170, 170, 170, 0.3); */
`;

const MainHeading = styled.span`
  font-size: 1.8rem;
  border-bottom: 1px solid cyan;
  box-sizing: border-box;
`;

const SettingsToggleOption = styled.span`
  box-sizing: border-box;
  border: 1px solid rgba(170, 170, 170, 0.3);
  padding: 0px 3px;
  color: #1b1b1b;
  background-color: rgba(170, 170, 170, 0.7);
  border-radius: 0%;
`;
