import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import { useSelectore, useDispatch } from "react-redux";

function TextInput(props) {
  const [currentWord, setCurrentWord] = useState({
    key: 1,
    id: "word-1",
    word: "",
    width: 0,
    left: 0,
  });

  const testContentScrollerCount = useRef(1);

  var textInput = document.getElementById("textInput");

  if (currentWord.word != "") {
    currentWordBlinker();
    textInput.focus();
  }
  if (
    currentWord.key > 1 &&
    document.getElementById(currentWord.id).offsetLeft -
      document.getElementById(currentWord.id).parentElement.offsetLeft ==
      5
  ) {
    testContentScroller();
  }
  useEffect(() => {
    setCurrentWord((prevWord) => ({
      ...prevWord,
      word: document.getElementById(currentWord.id).innerText,
      width: document.getElementById(currentWord.id).offsetWidth,
      left:
        document.getElementById(currentWord.id).offsetLeft -
        document.getElementById(currentWord.id).parentElement.offsetLeft,
    }));
  }, []);

  function currentWordBlinker() {
    if (textInput.value == "" || textInput.value == " ") {
      document
        .getElementById(currentWord.id)
        .classList.add("current-word-blinker");
    } else {
      document
        .getElementById(currentWord.id)
        .classList.remove("current-word-blinker");
    }
  }

  function testContentScroller() {
    let currentLineHeight = parseInt(
      window
        .getComputedStyle(document.getElementById("testContent"))
        .getPropertyValue("line-height")
    );
    document.getElementById(
      "testContentChild"
    ).style.transform = `translateY(-${
      currentLineHeight * testContentScrollerCount.current
    }px)`;
    testContentScrollerCount.current++;
  }

  function wordIncrementer(e) {
    let enteredValue = e.target.value.replace(/\s/g, "");
    textInput.value = "";
    document
      .getElementById(currentWord.id)
      .classList.remove("current-word-blinker");
    document.getElementById(currentWord.id).classList.remove("right-warning");
    document.getElementById(currentWord.id).classList.remove("wrong-warning");
    document.getElementById(currentWord.id).classList.remove("current-word");
    if (enteredValue == currentWord.word) {
      document.getElementById(currentWord.id).classList.add("correct");
    } else {
      document.getElementById(currentWord.id).classList.add("wrong");
      // wrongWordHandler(e);
    }
    document
      .getElementById(`word-${currentWord.key + 1}`)
      .classList.add("current-word");

    setCurrentWord((prevWord) => ({
      key: prevWord.key + 1,
      id: `word-${prevWord.key + 1}`,
      word: document.getElementById(`word-${prevWord.key + 1}`).innerText,
      width: document.getElementById(`word-${prevWord.key + 1}`).offsetWidth,
      left:
        document.getElementById(`word-${prevWord.key + 1}`).offsetLeft -
        document.getElementById(`word-${prevWord.key + 1}`).parentElement
          .offsetLeft,
    }));
  }

  function textInputEvaluater(e) {
    let enteredValue = e.target.value.replace(/\s/g, "");
    document.getElementById(currentWord.id).classList.remove("right-warning");
    document.getElementById(currentWord.id).classList.remove("wrong-warning");
    if (enteredValue != currentWord.word.slice(0, enteredValue.length)) {
      document.getElementById(currentWord.id).classList.add("wrong-warning");
    } else if (enteredValue == currentWord.word) {
      document.getElementById(currentWord.id).classList.add("right-warning");
    }
  }

  function textInputClickHandler(e) {
    if (e.keyCode == 32) {
      if (e.target.value == "" || e.target.value == " ") {
        textInput.value = "";
      } else {
        wordIncrementer(e);
      }
    } else {
      textInputEvaluater(e);
      currentWordBlinker();
    }
  }

  function wrongWordHandler(e) {
    let enteredValue = e.target.value.replace(/\s/g, "");
    let entValLen = enteredValue.length;
    let currWrdLen = currentWord.word.length;
    if (currWrdLen <= entValLen) {
      for (let i = 0; i < currWrdLen; i++) {
        if (currentWord.word[i] == enteredValue[i]) {
          document
            .getElementById(`${currentWord.id}-letter-${i}`)
            .classList.add("yellow");
        } else {
          document
            .getElementById(`${currentWord.id}-letter-${i}`)
            .classList.add("wrong");
        }
      }
      if (currWrdLen < entValLen) {
        let extraLetters = enteredValue.slice(currWrdLen);
        for (let i = 0; i < extraLetters.length; i++) {
          let newLetter = document
            .createElement("span")
            .appendChild(document.createTextNode(extraLetters[i]));
          newLetter.setAttribute(
            "id",
            `${currentWord.id}-letter-${currWrdLen + i}`
          );
          newLetter.setAttribute("class", "wrong");
          document.getElementById(currentWord.id).appendChild(newLetter);
        }
      }
    } else {
      for (let i = 0; i < currWrdLen; i++) {
        if (i < entValLen) {
          if (currentWord.word[i] == enteredValue[i]) {
            document
              .getElementById(`${currentWord.id}-letter-${i}`)
              .classList.add("yellow");
          } else {
            document
              .getElementById(`${currentWord.id}-letter-${i}`)
              .classList.add("wrong");
          }
        } else {
          document
            .getElementById(`${currentWord.id}-letter-${i}`)
            .classList.add("wrong");
        }
      }
    }
  }

  return (
    <Container width={`${currentWord.width}px`} left={`${currentWord.left}px`}>
      <input
        id="textInput"
        autoCapitalize="off"
        autoComplete="off"
        spellCheck="false"
        maxLength="15"
        onKeyDown={(e) =>
          setTimeout(() => {
            textInputClickHandler(e);
          }, 1)
        }
      ></input>
    </Container>
  );
}

export default TextInput;

const Container = styled.div`
  width: 100%;
  input {
    background-color: transparent;
    border: none;
    border-bottom: #aaa solid 2px;
    caret-color: #ffff;
    font-size: 1.2em;
    text-align: center;
    margin: 10px 0 20px;
    padding: 3px 3px 5px;
    color: #ffff;
    width: ${(props) => props.width};
    transform: translateX(${(props) => props.left});
    transition: all 150ms ease-in-out;

    &:focus {
      outline: none;
    }
  }
`;
